import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import CssBaseline from '@mui/material/CssBaseline';

// Context
import { AuthProvider } from './context/AuthContext';
import { ThemeContextProvider } from './context/ThemeContext';

// Components
import SimpleMobileNav from './components/navigation/SimpleMobileNav';
import HomePage from './components/home/HomePage';
import SmartJobDiscovery from './components/search/SmartJobDiscovery';
import LoginPage from './components/auth/LoginPage';
import Register from './components/Register';
import JobSearch from './components/JobSearch';
import ApplicationsPage from './pages/ApplicationsPage';
import JobApplyPage from './pages/JobApplyPage';
import JobDetailsPage from './pages/JobDetailsPage';
import EmployerApplicationsPage from './pages/EmployerApplicationsPage';
import EmployerPostJobPage from './pages/EmployerPostJobPageNew';
import ProfilePage from './components/profile/ProfilePage';
import ResumeBuilderPage from './components/candidate/ResumeBuilderPage';
import AboutUs from './components/AboutUs';
import DashboardRedirect from './components/DashboardRedirect';
import FeatureDemo from './pages/FeatureDemo';

// Utils
import { logDev } from './utils/logger';

// Protected Route Component
import ProtectedRoute from './components/ProtectedRoute';

// Simple Register Page wrapper
const RegisterPage = () => {
  return <Register />;
};

// Simple Job Search wrapper
const JobSearchPage = () => {
  return <JobSearch />;
};

function App() {
  useEffect(() => {
    logDev('info', 'JamDung Jobs App initialized', {
      timestamp: new Date().toISOString(),
      userAgent: navigator.userAgent,
      url: window.location.href
    });
  }, []);

  return (
    <AuthProvider>
      <ThemeContextProvider>
        <CssBaseline />
        <Helmet>
          <title>JamDung Jobs - Find Your Dream Job in Jamaica</title>
          <meta name="description" content="Jamaica's premier job search platform. Connect with top employers and find your dream job across the island." />
          <meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=no" />
        </Helmet>

        <Router>
          <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
            <SimpleMobileNav />

            <main style={{ flex: 1 }}>
              <Routes>
                {/* Public Routes */}
                <Route path="/" element={<HomePage />} />
                <Route path="/search" element={
                  <SmartJobDiscovery 
                    onSearch={(criteria) => console.warn('ðŸ” Search:', criteria)}
                    onClearFilters={() => console.warn('ðŸ§¹ Filters cleared')}
                  />
                } />
                <Route path="/login" element={<LoginPage />} />
                <Route path="/register" element={<RegisterPage />} />
                <Route path="/jobs" element={<JobSearchPage />} />
                <Route path="/jobs/:jobId" element={<JobDetailsPage />} />
                <Route path="/jobs/:jobId/apply" element={<JobApplyPage />} />
                <Route path="/about" element={<AboutUs />} />
                <Route path="/feature-demo" element={<FeatureDemo />} />

                {/* Protected Routes */}
                <Route
                  path="/dashboard"
                  element={
                    <ProtectedRoute>
                      <DashboardRedirect />
                    </ProtectedRoute>
                  }
                />

                {/* Employer Routes */}
                <Route
                  path="/employer/dashboard"
                  element={
                    <ProtectedRoute requiredRole="EMPLOYER">
                      <DashboardRedirect />
                    </ProtectedRoute>
                  }
                />
                <Route
                  path="/employer/jobs"
                  element={
                    <ProtectedRoute requiredRole="EMPLOYER">
                      <EmployerPostJobPage />
                    </ProtectedRoute>
                  }
                />
                <Route
                  path="/employer/applications"
                  element={
                    <ProtectedRoute requiredRole="EMPLOYER">
                      <EmployerApplicationsPage />
                    </ProtectedRoute>
                  }
                />
                <Route
                  path="/employer/profile"
                  element={
                    <ProtectedRoute requiredRole="EMPLOYER">
                      <ProfilePage />
                    </ProtectedRoute>
                  }
                />

                {/* Candidate Routes */}
                <Route
                  path="/candidate/dashboard"
                  element={
                    <ProtectedRoute requiredRole="CANDIDATE">
                      <DashboardRedirect />
                    </ProtectedRoute>
                  }
                />
                <Route
                  path="/applications"
                  element={
                    <ProtectedRoute requiredRole="CANDIDATE">
                      <ApplicationsPage />
                    </ProtectedRoute>
                  }
                />
                <Route
                  path="/profile"
                  element={
                    <ProtectedRoute requiredRole="CANDIDATE">
                      <ProfilePage />
                    </ProtectedRoute>
                  }
                />
                <Route
                  path="/resume-builder"
                  element={
                    <ProtectedRoute requiredRole="CANDIDATE">
                      <ResumeBuilderPage />
                    </ProtectedRoute>
                  }
                />

                {/* Catch all route */}
                <Route path="*" element={<HomePage />} />
              </Routes>
            </main>
          </div>
        </Router>
      </ThemeContextProvider>
    </AuthProvider>
  );
}

export default App;

import React, { useState, useEffect } from 'react';
import { FaTimes, FaFilter, FaRegBookmark, FaBookmark, FaClock, FaBuilding, FaMapMarkerAlt, FaBriefcase } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { logDev, logError, sanitizeForLogging } from '../utils/logging';
import { LocationAutocomplete } from './common/LocationAutocomplete';
import { JobTitleAutocomplete } from './common/JobTitleAutocomplete';
import { SalaryRangeAutocomplete } from './common/SalaryRangeAutocomplete';
import { CompanyAutocomplete } from './common/CompanyAutocomplete';
import { SkillsAutocomplete } from './common/SkillsAutocomplete';

const jobTypes = ['Full-time', 'Part-time', 'Contract', 'Internship', 'Remote'];
const sortOptions = [
  { value: 'latest', label: 'Latest' },
  { value: 'salary', label: 'Salary' },
  { value: 'relevance', label: 'Relevance' }
];

const QuickApplyModal = ({ job, onClose, onSuccess }) => {
  const [coverLetter, setCoverLetter] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    try {
      const response = await fetch(`http://localhost:5000/api/jobs/${job.id}/apply`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('jamdung_auth_token')}`
        },
        body: JSON.stringify({ coverLetter })
      });

      if (response.ok) {
        onSuccess();
      } else {
        const data = await response.json();
        throw new Error(data.message);
      }
    } catch (error) {
      logError('Failed to submit application', { 
        jobId: sanitizeForLogging(job.id),
        error: sanitizeForLogging(error)
      });
      alert(error.message || 'Failed to submit application');
    } finally {
      setSubmitting(false);
      onClose();
    }
  };

  return (
    <div 
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-[60]"
      onClick={(e) => e.stopPropagation()}
    >
      <div className="bg-white rounded-lg p-6 w-full max-w-md relative">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold">Quick Apply - {job.title}</h3>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <FaTimes />
          </button>
        </div>
        <form onSubmit={handleSubmit}>
          <textarea
            value={coverLetter}
            onChange={(e) => setCoverLetter(e.target.value)}
            placeholder="Why are you a good fit for this role? (Optional)"
            className="w-full p-3 border rounded-lg mb-4 min-h-[120px]"
          />
          <div className="flex justify-end">
            <button
              type="button"
              onClick={onClose}
              className="mr-2 px-4 py-2 text-gray-600 hover:text-gray-800"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={submitting}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
            >
              {submitting ? 'Submitting...' : 'Submit Application'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export const FindJobsModal = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const [searchParams, setSearchParams] = useState({
    search: '',
    location: '',
    jobType: '',
    minSalary: '',
    maxSalary: '',
    remote: false,
    sort: 'latest'
  });
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [savedJobs, setSavedJobs] = useState(new Set());
  const [selectedJob, setSelectedJob] = useState(null);
  const [showQuickApply, setShowQuickApply] = useState(false);

  const fetchJobs = async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams({
        ...searchParams,
        page,
        limit: 10
      });

      const response = await fetch(`http://localhost:5000/api/jobs?${queryParams}`);
      const data = await response.json();

      setJobs(data.jobs);
      setTotalPages(data.pagination.pages);

      // Fetch saved jobs if user is logged in
      if (user?.role === 'candidate') {
        const savedResponse = await fetch('http://localhost:5000/api/users/me', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('jamdung_auth_token')}`
          }
        });
        const userData = await savedResponse.json();
        setSavedJobs(new Set(userData.savedJobs || []));
      }
    } catch (error) {
      logError('Error fetching jobs', { 
        searchParams: sanitizeForLogging(searchParams),
        page: sanitizeForLogging(page),
        error: sanitizeForLogging(error)
      });
    } finally {
      setLoading(false);
    }
  };

  const toggleSaveJob = async (jobId) => {
    if (!user) {
      navigate('/login');
      return;
    }

    try {
      const action = savedJobs.has(jobId) ? 'unsave' : 'save';
      const response = await fetch(`http://localhost:5000/api/jobs/${jobId}/${action}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('jamdung_auth_token')}`
        }
      });

      if (response.ok) {
        setSavedJobs(prev => {
          const next = new Set(prev);
          if (action === 'save') next.add(jobId);
          else next.delete(jobId);
          return next;
        });
      }
    } catch (error) {
      logError('Error saving job', { 
        jobId: sanitizeForLogging(jobId),
        userId: sanitizeForLogging(user?.id),
        action: sanitizeForLogging(savedJobs.has(jobId) ? 'unsave' : 'save'),
        error: sanitizeForLogging(error)
      });
    }
  };

  const getJobStatusBadge = (job) => {
    const deadline = new Date(job.applicationDeadline);
    const now = new Date();
    const daysLeft = Math.ceil((deadline - now) / (1000 * 60 * 60 * 24));

    if (daysLeft < 0) return { text: 'Expired', color: 'red' };
    if (daysLeft <= 3) return { text: 'Closing Soon', color: 'orange' };
    if (job.featured) return { text: 'Featured', color: 'yellow' };
    if (job.remote) return { text: 'Remote', color: 'green' };
    return null;
  };

  const handleQuickApply = (job) => {
    if (!user) {
      navigate('/login');
      return;
    }
    setSelectedJob(job);
    setShowQuickApply(true);
  };

  useEffect(() => {
    if (isOpen) {
      logDev('Opening FindJobsModal', { 
        searchParams: sanitizeForLogging(searchParams),
        page: sanitizeForLogging(page)
      });
      fetchJobs();
    }
  }, [isOpen, page, searchParams]);

  const handleSearch = (e) => {
    e.preventDefault();
    logDev('Searching for jobs', { 
      searchParams: sanitizeForLogging(searchParams)
    });
    setPage(1);
    fetchJobs();
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prev => ({ ...prev, [name]: value }));
  };

  const viewJobDetails = (jobId) => {
    navigate(`/jobs/${jobId}`);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div 
        className="bg-white rounded-lg w-full max-w-4xl max-h-[90vh] overflow-hidden relative"
        onClick={(e) => e.stopPropagation()}
      >
        {showQuickApply && selectedJob && (
          <QuickApplyModal
            job={selectedJob}
            onClose={() => setShowQuickApply(false)}
            onSuccess={() => {
              setShowQuickApply(false);
              alert('Application submitted successfully!');
            }}
          />
        )}
        {/* Header */}
        <div className="p-4 border-b flex justify-between items-center">
          <h2 className="text-xl font-semibold">Find Jobs</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <FaTimes />
          </button>
        </div>

        {/* Search Form */}
        <form onSubmit={handleSearch} className="p-4 bg-gray-50">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <JobTitleAutocomplete
              value={searchParams.search}
              onChange={(value) => handleInputChange({ target: { name: 'search', value } })}
            />
            <LocationAutocomplete
              value={searchParams.location}
              onChange={(value) => handleInputChange({ target: { name: 'location', value } })}
            />
            <CompanyAutocomplete
              value={searchParams.company || ''}
              onChange={(value) => handleInputChange({ target: { name: 'company', value } })}
            />
          </div>

          {/* Advanced Filters */}
          <div className="mt-4 grid grid-cols-1 md:grid-cols-4 gap-4">
            <SalaryRangeAutocomplete
              type="min"
              value={searchParams.minSalary}
              onChange={(value) => handleInputChange({ target: { name: 'minSalary', value } })}
            />
            <SalaryRangeAutocomplete
              type="max"
              value={searchParams.maxSalary}
              onChange={(value) => handleInputChange({ target: { name: 'maxSalary', value } })}
            />
            <div className="relative">
              <select
                name="jobType"
                value={searchParams.jobType}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 appearance-none bg-white"
              >
                <option value="">All Job Types</option>
                {jobTypes.map(type => (
                  <option key={type} value={type}>{type}</option>
                ))}
              </select>
            </div>
            <div className="relative">
              <select
                name="sort"
                value={searchParams.sort}
                onChange={handleInputChange}
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 appearance-none bg-white"
              >
                {sortOptions.map(option => (
                  <option key={option.value} value={option.value}>{option.label}</option>
                ))}
              </select>
            </div>
            <div className="flex items-center space-x-4">
              <label className="flex items-center cursor-pointer">
                <input
                  type="checkbox"
                  name="remote"
                  checked={searchParams.remote}
                  onChange={(e) => handleInputChange({
                    target: { name: 'remote', value: e.target.checked }
                  })}
                  className="mr-2"
                />
                Remote Only
              </label>
            </div>
          </div>

          {/* Skills Filter */}
          <div className="mt-4">
            <SkillsAutocomplete
              value={searchParams.currentSkill || ''}
              onChange={(value) => {
                if (value && !searchParams.skills?.includes(value)) {
                  handleInputChange({
                    target: {
                      name: 'skills',
                      value: [...(searchParams.skills || []), value]
                    }
                  });
                }
                handleInputChange({ target: { name: 'currentSkill', value: '' } });
              }}
              selectedSkills={searchParams.skills || []}
            />
            {searchParams.skills && searchParams.skills.length > 0 && (
              <div className="mt-2 flex flex-wrap gap-2">
                {searchParams.skills.map(skill => (
                  <span
                    key={skill}
                    className="inline-flex items-center px-3 py-1 rounded-full text-sm
                      bg-blue-100 text-blue-800"
                  >
                    {skill}
                    <button
                      onClick={() => handleInputChange({
                        target: {
                          name: 'skills',
                          value: searchParams.skills.filter(s => s !== skill)
                        }
                      })}
                      className="ml-2 text-blue-600 hover:text-blue-800"
                    >
                      <FaTimes size={12} />
                    </button>
                  </span>
                ))}
              </div>
            )}
          </div>

          <div className="mt-4 flex justify-end space-x-2">
            <button
              type="button"
              onClick={() => setSearchParams({
                search: '',
                location: '',
                jobType: '',
                minSalary: '',
                maxSalary: '',
                remote: false,
                sort: 'latest'
              })}
              className="px-4 py-2 text-gray-600 hover:text-gray-800"
            >
              Clear Filters
            </button>
            <button
              type="submit"
              className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
            >
              Search Jobs
            </button>
          </div>
        </form>

        {/* Results */}
        <div className="p-4 overflow-y-auto" style={{ maxHeight: 'calc(90vh - 240px)' }}>
          {loading ? (
            <div className="flex justify-center items-center h-32">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
            </div>
          ) : jobs.length === 0 ? (
            <div className="text-center text-gray-500 py-8">
              No jobs found matching your criteria
            </div>
          ) : (
            <div className="space-y-4">
              {jobs.map(job => (
                <div
                  key={job.id}
                  className="border rounded-lg p-4 hover:shadow-md transition-shadow relative"
                >
                  <div className="flex justify-between items-start mb-2">
                    <div className="flex-1">
                      <h3 className="text-lg font-semibold text-blue-600 hover:text-blue-800 cursor-pointer" onClick={() => viewJobDetails(job.id)}>
                        {job.title}
                      </h3>
                      <div className="flex flex-wrap gap-2 mt-1">
                        {job.featured && (
                          <span className="bg-yellow-100 text-yellow-800 text-xs font-medium px-2.5 py-0.5 rounded">
                            Featured
                          </span>
                        )}
                        {job.remote && (
                          <span className="bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded">
                            Remote
                          </span>
                        )}
                        {job.applicationDeadline && (
                          <span className="flex items-center text-xs text-gray-500">
                            <FaClock className="mr-1" />
                            {new Date(job.applicationDeadline).toLocaleDateString()}
                          </span>
                        )}
                      </div>
                    </div>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        toggleSaveJob(job.id);
                      }}
                      className="text-gray-400 hover:text-blue-600 transition-colors"
                    >
                      {savedJobs.has(job.id) ? <FaBookmark /> : <FaRegBookmark />}
                    </button>
                  </div>

                  <div className="mt-2 text-sm text-gray-600">
                    <div className="flex items-center space-x-4 flex-wrap">
                      <span className="flex items-center">
                        <FaBuilding className="mr-1" /> {job.companyName}
                      </span>
                      <span className="flex items-center">
                        <FaMapMarkerAlt className="mr-1" /> {job.location}
                      </span>
                      <span className="flex items-center">
                        <FaBriefcase className="mr-1" /> {job.jobType}
                      </span>
                    </div>
                  </div>

                  <p className="mt-2 text-sm text-gray-500 line-clamp-2">{job.description}</p>

                  <div className="mt-3 flex justify-between items-center">
                    {job.salary && (
                      <div className="text-sm text-green-600 font-medium">
                        {job.salary}
                      </div>
                    )}
                    {user?.role === 'candidate' && (
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          handleQuickApply(job);
                        }}
                        className="px-4 py-1.5 bg-blue-600 text-white text-sm rounded hover:bg-blue-700 transition-colors"
                      >
                        Quick Apply
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="p-4 border-t flex justify-center space-x-2">
            <button
              onClick={() => setPage(p => Math.max(1, p - 1))}
              disabled={page === 1}
              className="px-4 py-2 border rounded-lg disabled:opacity-50 hover:bg-gray-50"
            >
              Previous
            </button>
            <span className="px-4 py-2">
              Page {page} of {totalPages}
            </span>
            <button
              onClick={() => setPage(p => Math.min(totalPages, p + 1))}
              disabled={page === totalPages}
              className="px-4 py-2 border rounded-lg disabled:opacity-50 hover:bg-gray-50"
            >
              Next
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  TextField,
  Button,
  Grid,
  Paper,
  FormControl,
  FormControlLabel,
  RadioGroup,
  Radio,
  Alert,
  CircularProgress,
  Divider,
  Fade
} from '@mui/material';
import { useAuth } from '../context/AuthContext';
import { JamaicaLocationAutocomplete } from './common/JamaicaLocationAutocomplete';
import GoogleOAuthButton from './auth/GoogleOAuthButton';

const Register = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { register, registerWithGoogle } = useAuth();

  // Check if we have a role in the location state (e.g., coming from "Post a Job")
  const defaultRole = location.state?.role || 'JOBSEEKER';
  const redirectPath = location.state?.from || '/dashboard';

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: defaultRole,
    // Company fields (only for employers)
    companyName: '',
    companyWebsite: '',
    companyLocation: '',
    companyDescription: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Set a message if coming from another page
  const [message, setMessage] = useState(location.state?.message || '');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleWebsiteBlur = () => {
    if (formData.companyWebsite && !/^https?:\/\//i.test(formData.companyWebsite)) {
      setFormData(prev => ({
        ...prev,
        companyWebsite: `https://${prev.companyWebsite}`
      }));
    }
  };

  const handleLocationSelect = (locationObj) => {
    if (!locationObj) {
      setFormData(prev => ({ ...prev, companyLocation: '' }));
      return;
    }
    const locationString = `${locationObj.name || locationObj.mainText}, ${locationObj.parish}, Jamaica`;
    setFormData(prev => ({ ...prev, companyLocation: locationString }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // Validation
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    if (formData.password.length < 8) {
      setError('Password must be at least 8 characters long');
      setLoading(false);
      return;
    }

    try {
      // Remove confirmPassword before sending to API and map field names to backend expectations
      const {
        confirmPassword,
        firstName,
        lastName,
        companyName,
        companyWebsite,
        companyLocation,
        companyDescription,
        ...rest
      } = formData;

      const registrationData = {
        ...rest,
        first_name: firstName,
        last_name: lastName,
        // Only include company fields for employers to avoid sending undefined
        ...(formData.role === 'EMPLOYER' && {
          company_name: companyName,
          company_website: companyWebsite,
          company_location: companyLocation,
          company_description: companyDescription
        })
      };

      await register(registrationData);

      // If we came from the employer job posting flow, redirect back there
      navigate(redirectPath);
    } catch (err) {
      setError(err.message || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

