    });

    // Force reload the image by adding a timestamp
    const photoUrlWithTimestamp = photoUrl ? `${photoUrl}?t=${new Date().getTime()}` : '';

    return (
    <Grid container spacing={3}>
      <Grid item xs={12} md={4}>
        <StyledPaper>
          <Box sx={{ textAlign: 'center', position: 'relative' }}>
            <Box sx={{ position: 'relative', display: 'inline-block', mb: 2 }}>
              {photoUrl ? (
                <Box sx={{ position: 'relative' }}>
                  <Avatar 
                    src={photoUrlWithTimestamp} 
                    alt={`${profile.firstName} ${profile.lastName}`}
                    sx={{ width: 120, height: 120, mb: 2 }}
                    imgProps={{ 
                      onError: (e) => {
                        console.error('Image failed to load:', photoUrlWithTimestamp);
                        e.target.onerror = null; // Prevent infinite error loop
                        e.target.src = ''; // Clear the src to show fallback
                      }
                    }}
                  />
                </Box>
              ) : (
                <Avatar
                  sx={{ width: 120, height: 120, mb: 2, bgcolor: '#009B77' }}
                >
                  {profile.firstName && profile.lastName ? 
                    `${profile.firstName[0]}${profile.lastName[0]}` : '?'}
                </Avatar>
              )}
              {editMode && (
                <label htmlFor="profile-picture-upload">
                  <IconButton 
                    component="span"
                    sx={{ 
                      position: 'absolute', 
                      bottom: 0, 
                      right: 0,
                      backgroundColor: '#009B77',
                      color: 'white',
                      '&:hover': { backgroundColor: '#007c5f' }
                    }}
                  >
                    <EditIcon fontSize="small" />
                  </IconButton>
                  <VisuallyHiddenInput
                    id="profile-picture-upload"
                    type="file"
                    accept="image/*"
                    onChange={handleProfilePictureUpload}
                  />
                </label>
              )}
            </Box>
            <Typography variant="h5" gutterBottom sx={{ fontWeight: 600, color: 'white' }}>
              {profile.firstName} {profile.lastName}
            </Typography>
            <Typography variant="body1" color="rgba(255, 255, 255, 0.7)" gutterBottom>
              {profile.title || 'Job Title'}
            </Typography>
            <Typography variant="body2" color="rgba(255, 255, 255, 0.7)" sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              {profile.location || 'Location'}
            </Typography>
          </Box>

          <Typography variant="h6" gutterBottom sx={{ fontWeight: 600, mt: 2, color: '#FFD700' }}>
            Contact Information
          </Typography>
          <Box sx={{ mb: 2 }}>
            <Typography variant="body2" color="rgba(255, 255, 255, 0.7)" gutterBottom>
              Email: {profile.email}
            </Typography>
            <Typography variant="body2" color="rgba(255, 255, 255, 0.7)" gutterBottom>
              Phone: {profile.phone || 'Not provided'}
            </Typography>
          </Box>

          <Typography variant="h6" gutterBottom sx={{ fontWeight: 600, mt: 2, color: '#FFD700' }}>
            Resume
          </Typography>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
            {profile.resumeFileName ? (
              <>
                <Typography variant="body2" color="rgba(255, 255, 255, 0.7)" gutterBottom>
                  {profile.resumeFileName}
                </Typography>
                <Box sx={{ display: 'flex', gap: 1 }}>
                  <StyledButton 
                    variant="outlined" 
                    size="small"
                    startIcon={<VisibilityIcon />}
                    onClick={handleViewResume}
                  >
                    View
                  </StyledButton>
                  <StyledButton 
                    variant="outlined" 
                    size="small"
                    startIcon={<GetAppIcon />}
                    onClick={handleDownloadResume}
                  >
                    Download
                  </StyledButton>
                </Box>
              </>
            ) : (
              <Typography variant="body2" color="rgba(255, 255, 255, 0.7)" gutterBottom>
                No resume uploaded
              </Typography>
            )}

            {editMode && (
              <Box sx={{ mt: 2 }}>
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                  <input
                    accept="application/pdf"
                    style={{ display: 'none' }}
                    id="resume-upload"
                    type="file"
                    onChange={handleFileSelect}
                  />
                  <label htmlFor="resume-upload">
                    <FileUploadButton 
                      component="span"
                      startIcon={<CloudUploadIcon />}
                      fullWidth
                    >
                      {profile.resumeFileName ? 'Update Resume' : 'Upload Resume'}
                    </FileUploadButton>
                  </label>

                  {selectedFile && (
                    <Box sx={{ 
                      mt: 1, 
                      p: 2, 
                      border: `1px solid ${theme.palette.primary.light}`,
                      borderRadius: 1,
                      backgroundColor: theme.palette.background.paper
                    }}>
                      <Typography variant="body2" gutterBottom>
                        <strong>Selected:</strong> {selectedFile.name}
                      </Typography>

                      {isUploading && (
                        <Box sx={{ width: '100%', mt: 1, mb: 2 }}>
                          <LinearProgress 
                            variant="determinate" 
                            value={uploadProgress} 
                            sx={{ 
                              height: 8, 
                              borderRadius: 4,
                              backgroundColor: theme.palette.primary.light,
                              '& .MuiLinearProgress-bar': {
                                backgroundColor: theme.palette.primary.main
                              }
                            }}
                          />
                          <Typography variant="caption" sx={{ display: 'block', textAlign: 'center', mt: 0.5 }}>
                            {uploadProgress}% Complete
                          </Typography>
                        </Box>
                      )}

                      <StyledButton 
                        variant="contained" 
                        size="small"
                        onClick={handleUploadResume}
                        disabled={isUploading}
                        fullWidth
                        sx={{ mt: 1 }}
                      >
                        {isUploading ? 'Uploading...' : 'Confirm Upload'}
                      </StyledButton>
                    </Box>
                  )}
                </Box>
              </Box>
            )}
          </Box>
        </StyledPaper>
      </Grid>

      <Grid item xs={12} md={8}>
        <StyledPaper>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
            <Typography variant="h6" sx={{ mb: 2, fontWeight: 600, color: '#FFD700' }}>
              About Me
            </Typography>
            {!editMode && (
              <StyledButton 
                variant="outlined" 
                size="small"
                startIcon={<EditIcon />}
                onClick={handleEditToggle}
              >
                Edit Profile
              </StyledButton>
            )}
          </Box>

          {editMode ? (
            <>
              <StyledTextField
                name="bio"
                label="Bio"
                multiline
                rows={4}
                value={editedProfile.bio || ''}
                onChange={handleProfileChange}
                fullWidth
                margin="normal"
                placeholder="Tell employers about yourself, your experience, and what you're looking for."
              />

              <Typography variant="h6" sx={{ mt: 3, mb: 2, fontWeight: 600, color: '#FFD700' }}>
                Personal Information
              </Typography>

              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <StyledTextField
                    name="firstName"
                    label="First Name"
                    value={editedProfile.firstName || ''}
                    onChange={handleProfileChange}
                    fullWidth
                    margin="normal"
                    placeholder="Your first name"
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <StyledTextField
                    name="lastName"
                    label="Last Name"
                    value={editedProfile.lastName || ''}
                    onChange={handleProfileChange}
                    fullWidth
                    margin="normal"
                    placeholder="Your last name"
                  />
                </Grid>
              </Grid>

              <Typography variant="h6" sx={{ mt: 3, mb: 2, fontWeight: 600, color: '#FFD700' }}>
                Contact Information
              </Typography>

              <StyledTextField
                name="phone"
                label="Phone Number"
                value={editedProfile.phone || ''}
                onChange={handleProfileChange}
                fullWidth
                margin="normal"
                placeholder="e.g., +1 (876) 555-1234"
              />
            </>
          ) : (
            <Typography variant="body1" paragraph sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
              {profile.bio || 'No bio provided. Edit your profile to add a bio.'}
            </Typography>
          )}

          <Typography variant="h6" sx={{ fontWeight: 600, mb: 2, color: '#FFD700' }}>
            Skills
          </Typography>

          <Box sx={{ display: 'flex', flexWrap: 'wrap', mb: 2 }}>
            {(() => {
              // Get the appropriate profile based on edit mode
              const currentProfile = editMode ? editedProfile : profile;

              // Get validated skills array using helper function
              const skills = getValidSkills(currentProfile);

              // Display message if no skills
              if (!skills || skills.length === 0) {
                return (
                  <Typography variant="body2" color="rgba(255, 255, 255, 0.7)">
                    No skills added yet.
                  </Typography>
                );
              }

              // Render skills chips
              return skills.map((skill, index) => (
                <StyledChip 
                  key={`skill-${index}-${skill}`}
                  label={skill}
                  onDelete={editMode ? undefined : undefined}
                />
              ));
            })()}
          </Box>

          {editMode && (
            <Box sx={{ mt: 2 }}>
              <SkillsAutocomplete
                value={getValidSkills(editedProfile)}
