                </Typography>
                <Button
                  startIcon={<AddIcon />}
                  onClick={addAchievement}
                  sx={{
                    color: '#2C5530',
                    '&:hover': {
                      backgroundColor: 'rgba(44, 85, 48, 0.1)'
                    }
                  }}
                >
                  Add Achievement
                </Button>
              </Box>

              {resumeData.achievements.map((achievement, index) => (
                <Box key={index} sx={{ mb: 3, position: 'relative' }}>
                  {index > 0 && (
                    <IconButton
                      size="small"
                      onClick={() => removeAchievement(index)}
                      sx={{
                        position: 'absolute',
                        top: 0,
                        right: 0,
                        color: 'rgba(255, 255, 255, 0.5)',
                        '&:hover': {
                          color: '#f44336',
                          backgroundColor: 'rgba(244, 67, 54, 0.1)'
                        }
                      }}
                    >
                      <DeleteIcon />
                    </IconButton>
                  )}

                  <Grid container spacing={2}>
                    <Grid item xs={12}>
                      <TextField
                        label="Achievement/Certification Title"
                        fullWidth
                        value={achievement.title}
                        onChange={(e) => handleAchievementChange(index, 'title', e.target.value)}
                        placeholder="e.g., Employee of the Year, AWS Certification"
                        sx={formStyles}
                      />
                    </Grid>

                    <Grid item xs={12}>
                      <TextField
                        label="Description"
                        fullWidth
                        multiline
                        rows={2}
                        value={achievement.description}
                        onChange={(e) => handleAchievementChange(index, 'description', e.target.value)}
                        placeholder="Provide details about this achievement or certification."
                        sx={formStyles}
                      />
                    </Grid>
                  </Grid>

                  {index < resumeData.achievements.length - 1 && (
                    <Divider sx={{ my: 2, backgroundColor: 'rgba(255, 215, 0, 0.2)' }} />
                  )}
                </Box>
              ))}
            </Grid>
          </Grid>
        );

      default:
        return null;
    }
  };

  return (
    <StyledPaper>
      <Typography variant="h5" sx={{ color: '#FFD700', mb: 3 }}>
        Resume Builder
      </Typography>

      <Stepper activeStep={activeStep} alternativeLabel sx={{ mb: 4 }}>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel
              sx={{
                '& .MuiStepLabel-label': {
                  color: activeStep === steps.indexOf(label) ? '#FFD700' : 'rgba(255, 255, 255, 0.7)'
                },
                '& .MuiStepIcon-root': {
                  color: activeStep === steps.indexOf(label) ? '#2C5530' : 'rgba(255, 255, 255, 0.3)'
                },
                '& .MuiStepIcon-root.Mui-completed': {
                  color: '#2C5530'
                }
              }}
            >
              {label}
            </StepLabel>
          </Step>
        ))}
      </Stepper>

      <Box sx={{ mt: 2, mb: 4 }}>
        {renderStepContent(activeStep)}
      </Box>

      <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
        <Button
          disabled={activeStep === 0}
          onClick={handleBack}
          startIcon={<NavigateBeforeIcon />}
          sx={{
            color: 'rgba(255, 255, 255, 0.7)',
            '&:hover': {
              backgroundColor: 'rgba(255, 255, 255, 0.1)'
            }
          }}
        >
          Back
        </Button>

        <Box>
          {activeStep === steps.length - 1 ? (
            <>
              <Button
                variant="contained"
                onClick={handleSave}
                sx={{
                  backgroundColor: '#2C5530',
                  color: '#FFFFFF',
                  mr: 2,
                  '&:hover': {
                    backgroundColor: '#1E3D23'
                  }
                }}
              >
                Save Resume
              </Button>
              <Button
                variant="contained"
                onClick={() => handleExport('pdf')}
                sx={{
                  backgroundColor: '#FFD700',
                  color: '#000000',
                  '&:hover': {
                    backgroundColor: '#E6C200'
                  }
                }}
              >
                Export as PDF
              </Button>
            </>
          ) : (
            <Button
              variant="contained"
              onClick={handleNext}
              endIcon={<NavigateNextIcon />}
              sx={{
                backgroundColor: '#2C5530',
                color: '#FFFFFF',
                '&:hover': {
                  backgroundColor: '#1E3D23'
                }
              }}
            >
              Next
            </Button>
          )}
        </Box>
      </Box>
    </StyledPaper>
  );
};

