import React, { useState, useEffect } from 'react';
import { FaBuilding } from 'react-icons/fa';
import { logDev, logError } from '../../utils/loggingUtils';
import { BaseAutocomplete } from './BaseAutocomplete';

export const CompanyAutocomplete = ({ value, onChange }) => {
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchCompanySuggestions = async (query) => {
    try {
      setLoading(true);
      const response = await fetch(`/api/companies/search?q=${encodeURIComponent(query)}`);
      const data = await response.json();
      return data.companies.map(company => ({
        id: company.id,
        name: company.name,
        logo: company.logo,
        location: company.location
      }));
    } catch (error) {
      logError('Error fetching company suggestions', error, {
        module: 'CompanyAutocomplete',
        function: 'fetchCompanySuggestions',
        query
      });
      return [];
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = async (input) => {
    onChange(input);
    if (input.length >= 2) {
      const results = await fetchCompanySuggestions(input);
      logDev('debug', 'Company suggestions fetched', {
        query: input,
        resultsCount: results.length
      });
      setSuggestions(results);
    } else {
      setSuggestions([]);
    }
  };

  const renderSuggestion = (suggestion) => (
    <div className="flex items-center">
      {suggestion.logo && (
        <img
          src={suggestion.logo}
          alt={suggestion.name}
          className="w-8 h-8 rounded-full mr-3 object-cover"
        />
      )}
      <div>
        <div className="font-medium">{suggestion.name}</div>
        {suggestion.location && (
          <div className="text-sm text-gray-500">{suggestion.location}</div>
        )}
      </div>
    </div>
  );

  return (
    <BaseAutocomplete
      value={value}
      onChange={(suggestion) => onChange(suggestion.name)}
      onClear={() => onChange('')}
      onInputChange={(input) => {
        handleInputChange(input);
        if (input.length >= 2) {
          logDev('debug', 'Company search input changed', { 
            inputLength: input.length 
          });
        }
      }}
      placeholder="Company name"
      icon={FaBuilding}
      suggestions={suggestions}
      renderSuggestion={renderSuggestion}
      loading={loading}
    />
  );
};

import React, { useState, useEffect } from 'react';
import { 
  TextField, 
  Autocomplete, 
  Box, 
  Typography, 
  Grid,
  Slider,
  InputAdornment,
  Chip
} from '@mui/material';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import { searchLocations, jamaicaParishes } from '../../data/jamaicaLocations';
import { logDev, logError } from '../../utils/loggingUtils';

/**
 * Jamaica-specific location autocomplete component
 * Uses local data of Jamaican parishes, cities, towns, and neighborhoods
 */
export const JamaicaLocationAutocomplete = ({ 
  value, 
  onChange, 
  placeholder = 'Location in Jamaica', 
  radius = 10, 
  onRadiusChange,
  sx = {}
}) => {
  const [inputValue, setInputValue] = useState('');
  const [options, setOptions] = useState([]);
  const [searchRadius, setSearchRadius] = useState(radius);
  const [selectedParishes, setSelectedParishes] = useState([]);

  // Update options when input changes
  useEffect(() => {
    if (inputValue.length >= 2) {
      const results = searchLocations(inputValue);
      logDev('debug', 'Jamaica location search results', {
        query: inputValue,
        resultsCount: results.length,
        topResults: results.slice(0, 3).map(r => r.name)
      });

      // Filter by selected parishes if any
      const filteredResults = selectedParishes.length > 0 
        ? results.filter(option => selectedParishes.includes(option.parish))
        : results;

      // Log parish filtering in development
      if (selectedParishes.length > 0) {
        logDev('debug', 'Filtering locations by parishes', {
          selectedParishes,
          beforeCount: results.length,
          afterCount: filteredResults.length
        });
      }

      // Always show at least top parishes if no results
      if (filteredResults.length === 0 && inputValue.length >= 2) {
        // Find parishes that match the input
        const matchingParishes = jamaicaParishes
          .filter(parish => parish.toLowerCase().includes(inputValue.toLowerCase()))
          .slice(0, 3);

        if (matchingParishes.length > 0) {
          const parishResults = matchingParishes.map(parish => ({
            mainText: parish,
            secondaryText: 'Jamaica',
            placeId: `jamaica-parish-${parish.toLowerCase().replace(/\s+/g, '-')}`,
            name: parish,
            parish: parish,
            type: 'parish',
            formattedAddress: `${parish}, Jamaica`
          }));

          setOptions(parishResults);
          return;
        }
      }

      setOptions(filteredResults);
    } else {
      // Show popular locations when input is empty or too short
      const popularLocations = [
        { name: 'Kingston', parish: 'Kingston' },
        { name: 'Montego Bay', parish: 'St. James' },
        { name: 'Ocho Rios', parish: 'St. Ann' },
        { name: 'Negril', parish: 'Westmoreland' },
        { name: 'Mandeville', parish: 'Manchester' }
      ].map(loc => ({
        mainText: loc.name,
        secondaryText: `${loc.parish}, Jamaica`,
        placeId: `jamaica-${loc.parish.toLowerCase().replace(/\s+/g, '-')}-${loc.name.toLowerCase().replace(/\s+/g, '-')}`,
        name: loc.name,
        parish: loc.parish,
        type: 'popular',
        formattedAddress: `${loc.name}, ${loc.parish}, Jamaica`
      }));

      setOptions(popularLocations);
    }
  }, [inputValue, selectedParishes]);

  // Handle input change
  const handleInputChange = (event, newInputValue) => {
    setInputValue(newInputValue);

    // Log input changes that meet the minimum length requirement
    if (newInputValue.length >= 2) {
      logDev('debug', 'Location search input changed', {
        input: newInputValue,
        length: newInputValue.length
      });
    }
  };

  // Handle option selection
  const handleOptionSelect = (event, option) => {
    if (option) {
      // Add radius to the selected location
      const locationWithRadius = {
        ...option,
        radius: searchRadius
      };

      // Log location selection in development
      logDev('info', 'Location selected', {
        name: option.name,
        parish: option.parish,
        placeId: option.placeId,
        radius: searchRadius
      });

      onChange(locationWithRadius);
    } else {
      logDev('debug', 'Location selection cleared');
      onChange(null);
    }
  };

  // Handle radius change
  const handleRadiusChange = (event, newValue) => {
    setSearchRadius(newValue);

    // If there's a selected location, update its radius
    if (value) {
      const updatedLocation = {
        ...value,
        radius: newValue
      };
      onChange(updatedLocation);
    }

    // Call the parent's radius change handler if provided
    if (onRadiusChange) {
      onRadiusChange(newValue);
    }

    // Log radius change in development
    logDev('debug', 'Search radius changed', {
      newRadius: newValue,
      hasSelectedLocation: !!value
    });
  };

  // Handle parish filter selection
  const handleParishSelect = (parish) => {
    setSelectedParishes(prev => {
      const newSelection = prev.includes(parish)
        ? prev.filter(p => p !== parish)
        : [...prev, parish];

      // Log parish filter change in development
      logDev('debug', 'Parish filter changed', {
        parish,
        action: prev.includes(parish) ? 'removed' : 'added',
        totalSelected: newSelection.length
      });

      return newSelection;
    });
  };

  return (
    <Box>
      <Autocomplete
        id="jamaica-location-autocomplete"
        options={options}
        getOptionLabel={(option) => typeof option === 'string' ? option : option.mainText}
        filterOptions={(x) => x} // Disable built-in filtering
        autoComplete
        includeInputInList
        filterSelectedOptions
        value={value && typeof value === 'object' ? value : null}
        onChange={handleOptionSelect}
        inputValue={inputValue}
        onInputChange={handleInputChange}
        noOptionsText={inputValue.length < 2 ? 'Type at least 2 characters' : 'No locations found'}
        renderInput={(params) => (
          <TextField
            {...params}
            label={placeholder}
            fullWidth
            InputProps={{
              ...params.InputProps,
              startAdornment: (
                <InputAdornment position="start">
                  <LocationOnIcon sx={{ color: '#FFD700' }} />
                </InputAdornment>
              ),
              sx: {
                color: 'white',
                backgroundColor: 'rgba(255, 255, 255, 0.08)',
                '& .MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(255, 215, 0, 0.5)',
                  borderWidth: '2px'
                },
                '&:hover .MuiOutlinedInput-notchedOutline': {
                  borderColor: 'rgba(255, 215, 0, 0.8)'
                },
                '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                  borderColor: '#FFD700',
                  borderWidth: '2px'
                },
                ...sx
              }
            }}
            InputLabelProps={{
              sx: { color: '#FFD700', fontWeight: 500 }
            }}
          />
        )}
        renderOption={(props, option) => (
          <li {...props}>
            <Grid container alignItems="center">
              <Grid item>
                <Box
                  component={LocationOnIcon}
                  sx={{ color: '#FFD700', mr: 2 }}
                />
              </Grid>
              <Grid item xs>
                <Typography variant="body1" color="text.primary">
                  {option.mainText}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {option.secondaryText}
                </Typography>
              </Grid>
            </Grid>
          </li>
        )}
      />

      {/* Parish filters */}
      <Box sx={{ mt: 1, mb: 2 }}>
        <Typography variant="body2" sx={{ color: '#FFD700', mb: 1, fontWeight: 500 }}>
          Filter by Parish:
        </Typography>
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
          {jamaicaParishes.map((parish) => (
            <Chip
              key={parish}
              label={parish}
              size="small"
              onClick={() => handleParishSelect(parish)}
              color={selectedParishes.includes(parish) ? 'primary' : 'default'}
              sx={{
                backgroundColor: selectedParishes.includes(parish) 
                  ? 'rgba(44, 85, 48, 0.8)' 
                  : 'rgba(255, 255, 255, 0.08)',
                color: selectedParishes.includes(parish) ? '#FFD700' : 'white',
                borderColor: selectedParishes.includes(parish) ? '#FFD700' : 'rgba(255, 255, 255, 0.3)',
                '&:hover': {
                  backgroundColor: selectedParishes.includes(parish) 
                    ? 'rgba(44, 85, 48, 0.9)' 
                    : 'rgba(255, 255, 255, 0.15)'
                }
              }}
            />
          ))}
        </Box>
      </Box>

      {/* Radius slider */}
      <Box sx={{ mt: 2 }}>
        <Typography id="radius-slider" gutterBottom sx={{ color: '#FFD700', fontWeight: 500 }}>
          Search Radius: {searchRadius} km
        </Typography>
        <Slider
          value={searchRadius}
          onChange={handleRadiusChange}
          aria-labelledby="radius-slider"
          valueLabelDisplay="auto"
          step={5}
          marks
          min={5}
          max={50}
          sx={{
            color: '#FFD700',
            '& .MuiSlider-thumb': {
              backgroundColor: '#FFD700',
              '&:hover, &.Mui-focusVisible': {
                boxShadow: '0px 0px 0px 8px rgba(255, 215, 0, 0.16)'
              }
            },
            '& .MuiSlider-track': {
              backgroundColor: '#FFD700'
            },
            '& .MuiSlider-rail': {
              backgroundColor: 'rgba(255, 215, 0, 0.3)'
            },
            '& .MuiSlider-mark': {
              backgroundColor: 'rgba(255, 215, 0, 0.5)'
            },
            '& .MuiSlider-valueLabel': {
              backgroundColor: '#2C5530',
              color: '#FFD700'
            }
          }}
        />
      </Box>
    </Box>
  );
};

import React, { useState, useEffect } from 'react';
import { 
  TextField, 
  Autocomplete, 
  Box, 
  Typography, 
  Grid,
  InputAdornment
} from '@mui/material';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import { jamaicaLocations, jamaicaParishes } from '../../data/jamaicaLocations';

// Function to log only in development environment
const logDev = (level, ...args) => {
  if (process.env.NODE_ENV !== 'production') {
    console[level](...args);
  }
};

/**
 * Simplified Jamaica-specific location autocomplete component for profile pages
 * Uses local data of Jamaican parishes, cities, towns, and neighborhoods
 * without the filtering options
 */
export const JamaicaLocationProfileAutocomplete = ({ 
  value, 
  onChange, 
  placeholder = 'Location in Jamaica', 
  sx = {}
}) => {
  const [inputValue, setInputValue] = useState('');
  const [options, setOptions] = useState([]);

  // Function to search locations based on input
  const searchLocations = (query) => {
    if (!query || query.length < 2) return [];

    const lowerQuery = query.toLowerCase();

    // Search through locations and parishes
    const results = jamaicaLocations
      .filter(location => 
        location.name.toLowerCase().includes(lowerQuery) || 
        location.parish.toLowerCase().includes(lowerQuery)
      )
      .map(location => ({
        mainText: location.name,
        secondaryText: `${location.parish}, Jamaica`,
        placeId: `jamaica-${location.parish.toLowerCase().replace(/\\s+/g, '-')}-${location.name.toLowerCase().replace(/\\s+/g, '-')}`,
        name: location.name,
        parish: location.parish,
        type: location.type || 'location',
        formattedAddress: `${location.name}, ${location.parish}, Jamaica`
      }));

    // Add parishes as options if they match
    const parishResults = jamaicaParishes
      .filter(parish => parish.toLowerCase().includes(lowerQuery))
      .map(parish => ({
        mainText: parish,
        secondaryText: 'Jamaica',
        placeId: `jamaica-parish-${parish.toLowerCase().replace(/\\s+/g, '-')}`,
        name: parish,
        parish: parish,
        type: 'parish',
        formattedAddress: `${parish}, Jamaica`
      }));

    // Merge and deduplicate options by mainText + secondaryText to ensure unique keys
    const merged = [...results, ...parishResults];
    const seen = new Set();
    const unique = [];
    for (const opt of merged) {
      const dedupKey = `${opt.mainText}|${opt.secondaryText}`;
      if (!seen.has(dedupKey)) {
        seen.add(dedupKey);
        unique.push(opt);
      }
    }

    return unique.slice(0, 10);
  };

  // Update options when input changes
  useEffect(() => {
    if (inputValue.length >= 2) {
      const results = searchLocations(inputValue);
      logDev('debug', 'Jamaica location search results:', results);
      setOptions(results);
    } else {
      // Build initial popular locations list and then deduplicate
      const initialPopular = [
        { name: 'Kingston', parish: 'Kingston' },
        { name: 'Montego Bay', parish: 'St. James' },
        { name: 'Ocho Rios', parish: 'St. Ann' },
        { name: 'Negril', parish: 'Westmoreland' },
        { name: 'Mandeville', parish: 'Manchester' },
        ...jamaicaParishes.map(parish => ({ name: parish, parish }))
      ].map(loc => ({
        mainText: loc.name,
        secondaryText: loc.name === loc.parish ? 'Jamaica' : `${loc.parish}, Jamaica`,
        placeId: `jamaica-${loc.parish.toLowerCase().replace(/\\s+/g, '-')}-${loc.name.toLowerCase().replace(/\\s+/g, '-')}`,
        name: loc.name,
        parish: loc.parish,
        type: loc.name === loc.parish ? 'parish' : 'popular',
        formattedAddress: loc.name === loc.parish ? `${loc.name}, Jamaica` : `${loc.name}, ${loc.parish}, Jamaica`
      }));

      // Deduplicate by mainText + secondaryText similar to search results
      const seenPopular = new Set();
      const uniquePopular = [];
      for (const opt of initialPopular) {
        const dedupKey = `${opt.mainText}|${opt.secondaryText}`;
        if (!seenPopular.has(dedupKey)) {
          seenPopular.add(dedupKey);
          uniquePopular.push(opt);
        }
      }

      setOptions(uniquePopular);
    }
  }, [inputValue]);

  // Handle input change
  const handleInputChange = (event, newInputValue) => {
    setInputValue(newInputValue);
  };

  // Handle option selection
  const handleOptionSelect = (event, option) => {
    if (option) {
      onChange(option);
    } else {
      onChange(null);
    }
  };

  return (
    <Autocomplete
      id="jamaica-location-profile-autocomplete"
      options={options}
      getOptionLabel={(option) => typeof option === 'string' ? option : option.mainText}
      filterOptions={(x) => x} // Disable built-in filtering
      autoComplete
      includeInputInList
      filterSelectedOptions
      value={value && typeof value === 'object' ? value : null}
      onChange={handleOptionSelect}
      inputValue={inputValue}
      onInputChange={handleInputChange}
      noOptionsText={inputValue.length < 2 ? 'Type at least 2 characters' : 'No locations found'}
      renderInput={(params) => (
        <TextField
          {...params}
          label={placeholder}
          fullWidth
          InputProps={{
            ...params.InputProps,
            startAdornment: (
              <InputAdornment position="start">
                <LocationOnIcon sx={{ color: '#FFD700' }} />
              </InputAdornment>
            ),
            sx: {
              color: 'white',
              backgroundColor: 'rgba(255, 255, 255, 0.08)',
              '& .MuiOutlinedInput-notchedOutline': {
                borderColor: 'rgba(255, 215, 0, 0.5)',
                borderWidth: '2px'
              },
              '&:hover .MuiOutlinedInput-notchedOutline': {
                borderColor: 'rgba(255, 215, 0, 0.8)'
              },
              '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                borderColor: '#FFD700',
                borderWidth: '2px'
              },
              ...sx
            }
          }}
          InputLabelProps={{
            sx: { color: '#FFD700', fontWeight: 500 }
          }}
        />
      )}
      renderOption={(props, option) => (
        <li {...props}>
          <Grid container alignItems="center">
            <Grid item>
              <Box
                component={LocationOnIcon}
                sx={{ color: '#FFD700', mr: 2 }}
              />
            </Grid>
            <Grid item xs>
              <Typography variant="body1" color="text.primary">
                {option.mainText}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {option.secondaryText}
              </Typography>
            </Grid>
          </Grid>
        </li>
      )}
    />
  );
};

