import { logDev, logError } from '../../utils/loggingUtils';

// Helper function to handle Google Maps API errors
const handleGoogleMapsError = (status) => {
  if (status === 'REQUEST_DENIED') {
    logError('Google Places API request denied', 
      { status, message: 'Please ensure the Places API is enabled in your Google Cloud Console' },
      { component: 'LocationAutocomplete' }
    );

    logDev('warn',
      'To enable the Places API:\n' +
      '1. Go to https://console.cloud.google.com\n' +
      '2. Select your project\n' +
      '3. Go to "APIs & Services" > "Library"\n' +
      '4. Search for "Places API"\n' +
      '5. Click "Enable"'
    );
    return true;
  }
  return false;
};

export const LocationAutocomplete = ({ value, onChange, placeholder = 'Location', radius, onRadiusChange }) => {
  const [inputValue, setInputValue] = useState('');
  const [options, setOptions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isLoaded, setIsLoaded] = useState(false);
  const [searchRadius, setSearchRadius] = useState(radius || 10); // Default 10 km
  const autocompleteRef = useRef(null);
  const inputRef = useRef(null);

  useEffect(() => {
    // Check if script is already loaded
    if (window.google?.maps?.places) {
      logDev('debug', 'Google Maps already loaded');
      setIsLoaded(true);
      initAutocomplete();
      return;
    }

    // Load Google Places API script
    const existingScript = document.getElementById('google-maps-script');
    if (existingScript) {
      logDev('debug', 'Script tag exists, waiting for load');
      return;
    }

    const script = document.createElement('script');
    script.id = 'google-maps-script';
    const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

    if (!apiKey) {
      logError('Google Maps API key is missing', null, { component: 'LocationAutocomplete' });
    }

    script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&libraries=places`;
    script.async = true;
    script.onerror = (error) => {
      logError('Failed to load Google Maps script', error, { component: 'LocationAutocomplete' });
      handleGoogleMapsError('REQUEST_DENIED');
    };
    script.onload = () => {
      logDev('debug', 'Google Maps script loaded successfully');
      setIsLoaded(true);
      initAutocomplete();
    };
    document.head.appendChild(script);

    return () => {
      // Don't remove the script on unmount as other components might need it
    };
  }, []);

  const initAutocomplete = () => {
    logDev('debug', 'Initializing autocomplete service');
    try {
      autocompleteRef.current = new window.google.maps.places.AutocompleteService();
      logDev('debug', 'Autocomplete service initialized successfully');
    } catch (error) {
      logError('Failed to initialize autocomplete service', error, { component: 'LocationAutocomplete' });
    }
  };

  const debouncedPredictions = useCallback(
    (() => {
      let timeoutId;
      return (input) => {
        if (timeoutId) {
          clearTimeout(timeoutId);
        }

        return new Promise((resolve) => {
          timeoutId = setTimeout(() => {
            if (!autocompleteRef.current) {
              logError('Autocomplete service not initialized', null, { component: 'LocationAutocomplete' });
              resolve([]);
              return;
            }

            logDev('debug', 'Requesting predictions for:', input);
            try {
              setLoading(true);
              autocompleteRef.current.getPlacePredictions(
                {
                  input,
                  types: ['(cities)', 'locality', 'sublocality', 'neighborhood']
                  // Allow locations in Jamaica by default, but don't restrict to only Jamaica
                  // componentRestrictions: { country: 'jm' }
                },
                (predictions, status) => {
                  setLoading(false);
                  resolve({ predictions, status });
                }
              );
            } catch (error) {
              logError('Error getting predictions', error, { 
                component: 'LocationAutocomplete',
                input: inputValue
              });
              setLoading(false);
              resolve({ predictions: [], status: 'ERROR' });
            }
          }, 300); // 300ms delay
        });
      };
    })(),
    []
  );

  const handleInputChange = async (event, newInputValue) => {
    setInputValue(newInputValue);

    if (newInputValue.length >= 2) {
      const { predictions, status } = await debouncedPredictions(newInputValue);
      handleAutocompleteResults(predictions, status);
    } else {
      setOptions([]);
    }
  };

  const handleAutocompleteResults = (predictions, status) => {
    logDev('debug', 'Got autocomplete results:', { status, predictionsCount: predictions?.length });

    // Check for specific API activation error
    if (handleGoogleMapsError(status)) {
      return;
    }

    try {
      if (status === window.google.maps.places.PlacesServiceStatus.OK && predictions) {
        const mappedOptions = predictions.map(p => ({
          id: p.place_id,
          description: p.description,
          mainText: p.structured_formatting.main_text,
          secondaryText: p.structured_formatting.secondary_text
        }));
        setOptions(mappedOptions);
      } else {
        logDev('debug', 'No valid predictions received, status:', status);
        setOptions([]);
      }
    } catch (error) {
      logError('Error processing predictions', error, { 
        component: 'LocationAutocomplete',
        status,
        predictionsCount: predictions?.length
      });
      setOptions([]);
    }
  };

  const handleOptionSelect = (event, option) => {
    if (option) {
      // Pass both the location name and the place_id to the parent component
      onChange({
        name: option.mainText,
        placeId: option.id,
        radius: searchRadius
      });
    } else {
      onChange(null);
    }
  };

  const handleRadiusChange = (event, newValue) => {
    setSearchRadius(newValue);

    // If we have a selected location, update the parent component with the new radius
    if (value && typeof value === 'object' && value.name) {
      onChange({
        ...value,
        radius: newValue
      });
    }

    // If onRadiusChange callback is provided, call it
    if (onRadiusChange) {
      onRadiusChange(newValue);
    }
  };

  // Initialize inputValue from value prop if it exists
  useEffect(() => {
    if (value && typeof value === 'object' && value.name) {
      setInputValue(value.name);
      if (value.radius) {
        setSearchRadius(value.radius);
      }
    } else if (typeof value === 'string') {
      setInputValue(value);
    }
  }, [value]);

  return (
    <Grid container spacing={2}>
      <Grid item xs={12}>
        <Autocomplete
          id="location-autocomplete"
          options={options}
          getOptionLabel={(option) => typeof option === 'string' ? option : option.mainText}
          filterOptions={(x) => x} // Disable built-in filtering
          autoComplete
          includeInputInList
          filterSelectedOptions
          value={value && typeof value === 'object' ? value : null}
          onChange={handleOptionSelect}
          inputValue={inputValue}
          onInputChange={handleInputChange}
          loading={loading}
          noOptionsText="No locations found"
          renderInput={(params) => (
            <TextField
              {...params}
              label={placeholder}
              fullWidth
              InputProps={{
                ...params.InputProps,
                startAdornment: (
                  <InputAdornment position="start">
                    <LocationOnIcon sx={{ color: '#FFD700' }} />
                  </InputAdornment>
                ),
                endAdornment: (
                  <React.Fragment>
                    {loading ? <CircularProgress color="inherit" size={20} /> : null}
                    {params.InputProps.endAdornment}
                  </React.Fragment>
                ),
                sx: {
                  color: 'white',
                  backgroundColor: 'rgba(255, 255, 255, 0.08)',
                  '& .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255, 215, 0, 0.5)',
                    borderWidth: '2px'
                  },
                  '&:hover .MuiOutlinedInput-notchedOutline': {
                    borderColor: 'rgba(255, 215, 0, 0.8)'
                  },
                  '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                    borderColor: '#FFD700',
                    borderWidth: '2px'
                  }
                }
              }}
              InputLabelProps={{
                sx: { color: '#FFD700', fontWeight: 500 }
              }}
            />
          )}
          renderOption={(props, option) => (
            <li {...props}>
              <Grid container alignItems="center">
                <Grid item>
                  <Box
                    component={LocationOnIcon}
                    sx={{ color: 'text.secondary', mr: 2 }}
                  />
                </Grid>
                <Grid item xs>
                  <Typography variant="body1" color="text.primary">
                    {option.mainText}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {option.secondaryText}
                  </Typography>
                </Grid>
              </Grid>
            </li>
          )}
        />
      </Grid>

      {/* Radius slider */}
      <Grid item xs={12}>
        <Typography id="radius-slider" gutterBottom sx={{ color: '#FFD700', fontWeight: 500 }}>
          Search Radius: {searchRadius} km
        </Typography>
        <Slider
          value={searchRadius}
          onChange={handleRadiusChange}
          aria-labelledby="radius-slider"
          valueLabelDisplay="auto"
          step={5}
          marks
          min={5}
          max={50}
          sx={{
            color: '#FFD700',
            '& .MuiSlider-thumb': {
              backgroundColor: '#FFD700',
              '&:hover, &.Mui-focusVisible': {
                boxShadow: '0px 0px 0px 8px rgba(255, 215, 0, 0.16)'
              }
            },
            '& .MuiSlider-track': {
              backgroundColor: '#FFD700'
            },
            '& .MuiSlider-rail': {
              backgroundColor: 'rgba(255, 215, 0, 0.3)'
            },
            '& .MuiSlider-mark': {
              backgroundColor: 'rgba(255, 215, 0, 0.5)'
            },
            '& .MuiSlider-valueLabel': {
              backgroundColor: '#2C5530',
              color: '#FFD700'
            }
          }}
        />
      </Grid>
    </Grid>
  );
};

import React, { useState, useEffect } from 'react';
import { FaClock, FaMapMarkerAlt, FaBuilding } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

const RecentlyViewedJobs = ({ className = '', limit = 5 }) => {
  const [recentJobs, setRecentJobs] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    loadRecentJobs();
  }, []);

  const loadRecentJobs = () => {
    try {
      const recent = JSON.parse(localStorage.getItem('recentlyViewedJobs') || '[]');
      setRecentJobs(recent.slice(0, limit));
    } catch (error) {
      console.error('Error loading recent jobs:', error);
      setRecentJobs([]);
    }
  };

  const handleJobClick = (job) => {
    navigate(`/jobs/${job.id}`);
  };

  const clearRecentJobs = () => {
    localStorage.removeItem('recentlyViewedJobs');
    setRecentJobs([]);
  };

  if (recentJobs.length === 0) {
    return null;
  }

  return (
    <div className={`bg-white rounded-lg border border-gray-200 p-4 ${className}`}>
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
          <FaClock className="w-5 h-5 text-gray-500" />
          Recently Viewed
        </h3>
        <button
          onClick={clearRecentJobs}
          className="text-sm text-gray-500 hover:text-gray-700 underline"
        >
          Clear
        </button>
      </div>

      <div className="space-y-3">
        {recentJobs.map((job, index) => (
          <div
            key={`${job.id}-${index}`}
            onClick={() => handleJobClick(job)}
            className="flex items-start gap-3 p-3 rounded-lg border border-gray-100 hover:border-gray-300 hover:bg-gray-50 cursor-pointer transition-all"
          >
            {/* Company Logo */}
            <div className="flex-shrink-0">
              {job.company?.logoUrl ? (
                <img
                  src={job.company.logoUrl}
                  alt={`${job.company.name} logo`}
                  className="w-10 h-10 rounded-lg object-cover"
                />
              ) : (
                <div className="w-10 h-10 bg-gray-200 rounded-lg flex items-center justify-center">
                  <FaBuilding className="w-5 h-5 text-gray-400" />
                </div>
              )}
            </div>

            {/* Job Details */}
            <div className="flex-1 min-w-0">
              <h4 className="text-sm font-medium text-gray-900 truncate">
                {job.title}
              </h4>
              <p className="text-sm text-gray-600 truncate">
                {job.company?.name || 'Company'}
              </p>
              <div className="flex items-center gap-4 mt-1 text-xs text-gray-500">
                {job.location && (
                  <span className="flex items-center gap-1">
                    <FaMapMarkerAlt className="w-3 h-3" />
                    {job.location}
                  </span>
                )}
                {job.type && (
                  <span className="capitalize">
                    {job.type.replace('_', ' ').toLowerCase()}
                  </span>
                )}
              </div>
            </div>

            {/* View Time */}
            <div className="flex-shrink-0 text-xs text-gray-400">
              {job.viewedAt && (
                <span>{getTimeAgo(job.viewedAt)}</span>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Utility function to track job views
export const trackJobView = (job) => {
  try {
    const recentJobs = JSON.parse(localStorage.getItem('recentlyViewedJobs') || '[]');

    // Remove existing entry for this job if it exists
    const filteredJobs = recentJobs.filter(item => item.id !== job.id);

    // Add current job to the beginning with timestamp
    const updatedJobs = [
      {
        ...job,
        viewedAt: new Date().toISOString()
      },
      ...filteredJobs
    ].slice(0, 10); // Keep only last 10 jobs

    localStorage.setItem('recentlyViewedJobs', JSON.stringify(updatedJobs));
  } catch (error) {
    console.error('Error tracking job view:', error);
  }
};

// Helper function to format time ago
const getTimeAgo = (dateString) => {
  const now = new Date();
  const viewed = new Date(dateString);
  const diffInMinutes = Math.floor((now - viewed) / (1000 * 60));

  if (diffInMinutes < 1) return 'Just now';
  if (diffInMinutes < 60) return `${diffInMinutes}m ago`;

  const diffInHours = Math.floor(diffInMinutes / 60);
  if (diffInHours < 24) return `${diffInHours}h ago`;

  const diffInDays = Math.floor(diffInHours / 24);
  if (diffInDays < 7) return `${diffInDays}d ago`;

  return viewed.toLocaleDateString();
};

export default RecentlyViewedJobs;

