import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Button,
  CircularProgress,
  Alert,
  Tabs,
  Tab,
  Fade,
  Tooltip,
  IconButton,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Grid
} from '@mui/material';
import VisibilityIcon from '@mui/icons-material/Visibility';
import DownloadIcon from '@mui/icons-material/Download';
import DeleteIcon from '@mui/icons-material/Delete';
import axios from 'axios';
import { format } from 'date-fns';
import { buildApiUrl } from '../../config';
import { logDev, logError, sanitizeForLogging } from '../../utils/loggingUtils';

const ApplicationsList = () => {
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [tabValue, setTabValue] = useState(0);
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);

  useEffect(() => {
    fetchApplications();
  }, []);

  const fetchApplications = async () => {
    setLoading(true);
    try {
      // Get the token directly from localStorage
      const token = localStorage.getItem('jamdung_auth_token');

      // Log the token presence (not the actual token)
      logDev('debug', 'Fetching applications - pre request', { 
        hasToken: !!token,
        tokenLength: token ? token.length : 0,
        endpoint: '/api/jobseeker/applications' 
      });

      // If no token is available, throw an error to be handled
      if (!token) {
        throw new Error('Authentication token not found. Please log in again.');
      }

      // Create a specific instance with the token for this request
      const headers = {
        'Authorization': `Bearer ${token}`
      };

      // Make the request with explicit headers
      const response = await axios.get(buildApiUrl('/api/jobseeker/applications'), { headers });

      // Add more detailed response logging
      logDev('debug', 'Applications fetched successfully', { 
        count: response.data?.applications?.length || 0,
        hasApplications: Array.isArray(response.data?.applications),
        responseStatus: response.status,
        contentType: response.headers?.['content-type']
      });

      // Log the full response structure (sanitized)
      logDev('debug', 'Applications response structure', {
        dataKeys: Object.keys(response.data || {}),
        isApplicationsArray: Array.isArray(response.data?.applications),
        firstApplication: response.data?.applications?.[0] ? 
          { id: response.data.applications[0].id, status: response.data.applications[0].status } : null
      });

      setApplications(response.data.applications || []);
      // Clear any previous errors
      setError(null);
    } catch (err) {
      // Add more detailed error logging
      logError('Error fetching applications', err, {
        module: 'ApplicationsList',
        function: 'fetchApplications',
        endpoint: '/api/jobseeker/applications',
        errorStatus: err.response?.status,
        errorData: sanitizeForLogging(err.response?.data),
        errorMessage: err.message,
        hasResponse: !!err.response
      });

      // Handle different error cases
      if (err.message === 'Authentication token not found. Please log in again.') {
        setError('Please log in to view your applications.');
      } else if (err.response && err.response.status === 401) {
        setError('Your session has expired. Please log in again.');
      } else if (err.response && err.response.status !== 404) {
        setError('Failed to load your applications. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleViewDetails = (application) => {
    setSelectedApplication(application);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  const handleDownloadResume = async (applicationId, resumeUrl) => {
    try {
      const response = await axios.get(resumeUrl, {
        responseType: 'blob'
      });

      // Create a blob link to download
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `resume-${applicationId}.pdf`);
      document.body.appendChild(link);
      link.click();

      // Clean up
      link.parentNode.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (err) {
      logError('Error downloading resume', err, sanitizeForLogging({
        module: 'ApplicationsList',
        function: 'handleDownloadResume',
        applicationId,
        errorStatus: err.response?.status
      }));
      setError('Failed to download resume. Please try again.');
    }
  };

  const handleDeleteApplication = async (applicationId) => {
    if (window.confirm('Are you sure you want to withdraw this application? This action cannot be undone.')) {
      try {
        await axios.delete(buildApiUrl(`/applications/${applicationId}`));
        // Remove from state
        setApplications(prev => prev.filter(app => app.id !== applicationId));
        logDev('debug', 'Application withdrawn successfully', sanitizeForLogging({ 
          applicationId,
          action: 'withdraw'
        }));
      } catch (err) {
        logError('Error withdrawing application', err, sanitizeForLogging({
          module: 'ApplicationsList',
          function: 'handleDeleteApplication',
          applicationId,
          errorStatus: err.response?.status,
          action: 'withdraw'
        }));
        setError('Failed to withdraw application. Please try again.');
      }
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'APPLIED':
        return { bg: 'rgba(21, 101, 192, 0.2)', color: '#64b5f6' }; // Blue
      case 'PENDING':
        return { bg: 'rgba(21, 101, 192, 0.2)', color: '#64b5f6' }; // Blue
      case 'REVIEWING':
        return { bg: 'rgba(245, 127, 23, 0.2)', color: '#ffd54f' }; // Amber
      case 'INTERVIEW':
        return { bg: 'rgba(46, 125, 50, 0.2)', color: '#81c784' }; // Green
      case 'OFFERED':
        return { bg: 'rgba(123, 31, 162, 0.2)', color: '#ce93d8' }; // Purple
      case 'REJECTED':
        return { bg: 'rgba(198, 40, 40, 0.2)', color: '#ef9a9a' }; // Red
      case 'WITHDRAWN':
        return { bg: 'rgba(97, 97, 97, 0.2)', color: '#bdbdbd' }; // Grey
      default:
        return { bg: 'rgba(33, 33, 33, 0.2)', color: '#e0e0e0' }; // Default grey
    }
  };

  const formatDate = (dateString) => {
    return format(new Date(dateString), 'MMM dd, yyyy');
  };

  const filteredApplications = applications.filter(app => {
    if (tabValue === 0) return true; // All applications
    if (tabValue === 1) return ['PENDING', 'APPLIED', 'REVIEWING', 'INTERVIEW'].includes(app.status); // Active
    if (tabValue === 2) return ['OFFERED'].includes(app.status); // Offered
    if (tabValue === 3) return ['REJECTED', 'WITHDRAWN'].includes(app.status); // Closed
    return true;
  });

  return (
    <Fade in={true} timeout={800}>
      <Box sx={{ color: 'white' }}>
        {/* Title moved to parent ApplicationsPage component */}

        {error && <Alert severity="error" sx={{ mb: 3 }}>{error}</Alert>}

        <Box sx={{ borderBottom: 1, borderColor: 'rgba(255, 215, 0, 0.3)', mb: 3 }}>
          <Tabs
            value={tabValue}
            onChange={handleTabChange}
            variant="scrollable"
            scrollButtons="auto"
            sx={{
              '& .MuiTabs-indicator': {
                backgroundColor: '#FFD700'
              }
            }}
          >
            <Tab label="All Applications" sx={{ color: 'rgba(255, 255, 255, 0.7)', '&.Mui-selected': { color: '#FFD700' } }} />
            <Tab label="Active" sx={{ color: 'rgba(255, 255, 255, 0.7)', '&.Mui-selected': { color: '#FFD700' } }} />
            <Tab label="Offered" sx={{ color: 'rgba(255, 255, 255, 0.7)', '&.Mui-selected': { color: '#FFD700' } }} />
            <Tab label="Closed" sx={{ color: 'rgba(255, 255, 255, 0.7)', '&.Mui-selected': { color: '#FFD700' } }} />
          </Tabs>
        </Box>

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
            <CircularProgress sx={{ color: '#FFD700' }} />
          </Box>
        ) : error ? (
          <Alert severity="error" sx={{ 
            mb: 3,
            backgroundColor: 'rgba(30, 30, 30, 0.9)',
            color: '#ff6b6b',
            border: '1px solid #ff6b6b'
          }}>
            {error}
          </Alert>
        ) : filteredApplications.length === 0 ? (
          <Paper 
            elevation={1} 
            sx={{ 
              p: 4, 
              textAlign: 'center',
              borderRadius: 2,
              backgroundColor: 'rgba(249, 249, 249, 0.9)',
              border: '1px solid rgba(255, 215, 0, 0.1)',
              backdropFilter: 'blur(5px)'
            }}
          >
            <Box sx={{ mb: 3 }}>
              <img 
                src="/images/icons/application-empty.svg" 
                alt="No applications" 
                style={{ width: '100px', height: '100px', opacity: 0.7 }} 
                onError={(e) => {
                  e.target.src = 'https://img.icons8.com/fluency/96/null/document.png';
                }}
              />
            </Box>
            <Typography variant="h6" sx={{ color: '#2C5530', fontWeight: 600 }} gutterBottom>
              {tabValue === 0 ? 'Ready to start your career journey?' : 'Nothing here yet'}
            </Typography>
            <Typography variant="body1" color="textSecondary" paragraph sx={{ maxWidth: '500px', mx: 'auto' }}>
              {tabValue === 0 
                ? 'Your job applications will appear here once you start applying. Browse available jobs to find your perfect match in Jamaica.' 
                : "You don't have any applications in this category yet. Keep applying to see updates here."}
            </Typography>
            <Button 
              variant="contained"
              href="/jobs"
              sx={{
                mt: 2,
                background: 'linear-gradient(90deg, #2C5530, #FFD700)',
                color: '#000',
                '&:hover': {
                  background: 'linear-gradient(90deg, #FFD700, #2C5530)',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 12px rgba(255, 215, 0, 0.3)'
                },
                transition: 'all 0.3s ease',
                textTransform: 'none',
                fontWeight: 600,
                px: 3,
                py: 1.2
              }}
            >
              Explore Jobs
            </Button>
          </Paper>
        ) : (
          <TableContainer component={Paper} sx={{ 
            mb: 4, 
            backgroundColor: 'rgba(20, 20, 20, 0.85)',
            border: '1px solid rgba(255, 215, 0, 0.3)',
            backdropFilter: 'blur(10px)',
            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.5)'
          }}>
            <Table sx={{ '& .MuiTableCell-root': { borderColor: 'rgba(255, 215, 0, 0.1)' } }}>
              <TableHead sx={{ backgroundColor: 'rgba(0, 0, 0, 0.3)' }}>
                <TableRow>
                  <TableCell sx={{ color: '#FFD700', fontWeight: 600 }}>Job Title</TableCell>
                  <TableCell sx={{ color: '#FFD700', fontWeight: 600 }}>Company</TableCell>
                  <TableCell sx={{ color: '#FFD700', fontWeight: 600 }}>Applied On</TableCell>
                  <TableCell sx={{ color: '#FFD700', fontWeight: 600 }}>Status</TableCell>
                  <TableCell align="right" sx={{ color: '#FFD700', fontWeight: 600 }}>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredApplications.map((application) => {
                  const statusStyle = getStatusColor(application.status);
                  return (
                    <TableRow key={application.id} hover>
                      <TableCell sx={{ color: 'white' }}>{application.job.title}</TableCell>
                      <TableCell sx={{ color: 'white' }}>{application.job.company.name}</TableCell>
                      <TableCell sx={{ color: 'white' }}>{formatDate(application.createdAt)}</TableCell>
                      <TableCell>
                        <Chip 
                          label={application.status.replace('_', ' ')} 
                          sx={{ 
                            backgroundColor: statusStyle.bg,
                            color: statusStyle.color,
                            fontWeight: 600
                          }} 
                        />
                      </TableCell>
                      <TableCell>
                        <Box sx={{ display: 'flex', gap: 1 }}>
                          <Tooltip title="View Details">
                            <IconButton 
                              size="small" 
                              onClick={() => handleViewDetails(application)}
                              sx={{ color: '#FFD700' }}
                            >
                              <VisibilityIcon fontSize="small" />
                            </IconButton>
                          </Tooltip>
                          <Tooltip title="Download Resume">
                            <IconButton 
                              size="small" 
                              onClick={() => handleDownloadResume(application.id, application.resumeUrl)}
                              sx={{ color: '#007E1B' }}
                            >
                              <DownloadIcon fontSize="small" />
                            </IconButton>
                          </Tooltip>
                          {['PENDING', 'APPLIED', 'REVIEWING'].includes(application.status) && (
                            <Tooltip title="Withdraw Application">
                              <IconButton 
                                size="small" 
                                onClick={() => handleDeleteApplication(application.id)}
                                sx={{ color: '#ff6b6b' }}
                              >
                                <DeleteIcon fontSize="small" />
                              </IconButton>
                            </Tooltip>
                          )}
                        </Box>
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </TableContainer>
        )}

        {/* Application Details Dialog */}
        <Dialog
          open={openDialog}
          onClose={handleCloseDialog}
          maxWidth="md"
          fullWidth
          PaperProps={{
            style: {
              backgroundColor: 'rgba(20, 20, 20, 0.95)',
              border: '1px solid rgba(255, 215, 0, 0.3)',
              boxShadow: '0 8px 32px rgba(0, 0, 0, 0.5)',
              color: 'white'
            }
          }}
        >
          {selectedApplication && (
            <>
              <DialogTitle sx={{ 
                fontWeight: 700, 
                borderBottom: '1px solid rgba(255, 215, 0, 0.3)',
                pb: 2,
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                color: '#FFD700'
              }}>
                Application Details
              </DialogTitle>
              <DialogContent sx={{ mt: 2 }}>
                <Grid container spacing={3}>
                  <Grid item xs={12} md={6}>
                    <Typography variant="subtitle2" sx={{ color: 'rgba(255, 215, 0, 0.7)' }}>Job Title</Typography>
                    <Typography variant="body1" sx={{ fontWeight: 600, mb: 2, color: 'white' }}>
                      {selectedApplication.job.title}
                    </Typography>
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <Typography variant="subtitle2" sx={{ color: 'rgba(255, 215, 0, 0.7)' }}>Company</Typography>
                    <Typography variant="body1" sx={{ fontWeight: 600, mb: 2, color: 'white' }}>
                      {selectedApplication.job.company.name}
                    </Typography>
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <Typography variant="subtitle2" sx={{ color: 'rgba(255, 215, 0, 0.7)' }}>Applied On</Typography>
                    <Typography variant="body1" sx={{ mb: 2, color: 'white' }}>
                      {formatDate(selectedApplication.createdAt)}
                    </Typography>
                  </Grid>
                  <Grid item xs={12} md={6}>
                    <Typography variant="subtitle2" sx={{ color: 'rgba(255, 215, 0, 0.7)' }}>Status</Typography>
                    <Chip 
                      label={selectedApplication.status.replace('_', ' ')} 
                      sx={{ 
                        backgroundColor: getStatusColor(selectedApplication.status).bg,
                        color: getStatusColor(selectedApplication.status).color,
                        fontWeight: 600,
                        mb: 2
                      }} 
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <Typography variant="subtitle2" sx={{ color: 'rgba(255, 215, 0, 0.7)' }}>Cover Letter</Typography>
                    <Paper variant="outlined" sx={{ p: 2, mb: 2, backgroundColor: 'rgba(30, 30, 30, 0.7)', border: '1px solid rgba(255, 215, 0, 0.2)' }}>
                      <Typography variant="body1" sx={{ whiteSpace: 'pre-line', color: 'white' }}>
                        {selectedApplication.coverLetter || 'No cover letter provided'}
                      </Typography>
                    </Paper>
                  </Grid>
                  {selectedApplication.additionalInfo && (
                    <Grid item xs={12}>
                      <Typography variant="subtitle2" sx={{ color: 'rgba(255, 215, 0, 0.7)' }}>Additional Information</Typography>
                      <Paper variant="outlined" sx={{ p: 2, mb: 2, backgroundColor: 'rgba(30, 30, 30, 0.7)', border: '1px solid rgba(255, 215, 0, 0.2)' }}>
                        <Typography variant="body1" sx={{ whiteSpace: 'pre-line', color: 'white' }}>
                          {selectedApplication.additionalInfo}
                        </Typography>
                      </Paper>
                    </Grid>
                  )}
                </Grid>
              </DialogContent>
              <DialogActions sx={{ p: 2, borderTop: '1px solid rgba(255, 215, 0, 0.3)' }}>
                <Button 
                  onClick={handleCloseDialog}
                  sx={{
                    color: '#FFD700',
                    borderColor: '#FFD700',
                    border: '1px solid',
                    '&:hover': {
                      backgroundColor: 'rgba(255, 215, 0, 0.1)'
                    }
                  }}
                >
                  Close
                </Button>
                <Button 
                  variant="contained"
                  onClick={() => handleDownloadResume(selectedApplication.id, selectedApplication.resumeUrl)}
                  startIcon={<DownloadIcon />}
                  sx={{
                    background: 'linear-gradient(45deg, #007E1B 30%, #009921 90%)',
                    color: 'white',
                    boxShadow: '0 2px 8px rgba(0, 126, 27, 0.5)',
                    '&:hover': {
                      background: 'linear-gradient(45deg, #005714 30%, #007E1B 90%)',
                      boxShadow: '0 4px 12px rgba(0, 126, 27, 0.7)'
                    },
                    textTransform: 'none'
                  }}
                >
                  Download Resume
                </Button>
              </DialogActions>
            </>
          )}
        </Dialog>
      </Box>
    </Fade>
  );
};

export default ApplicationsList;

import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  Box,
  TextField,
  CircularProgress,
  Divider,
  Alert,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  LinearProgress
} from '@mui/material';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import WarningIcon from '@mui/icons-material/Warning';
import InfoIcon from '@mui/icons-material/Info';
import api from '../../utils/api';
import { buildApiUrl } from '../../config';
import { useAuth } from '../../context/AuthContext';
import { logDev, logError, sanitizeForLogging } from '../../utils/loggingUtils';

const QuickApplyModal = ({ open, onClose, job, onSuccess }) => {
  const { currentUser } = useAuth();
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [alreadyApplied, setAlreadyApplied] = useState(false);
  const [profileData, setProfileData] = useState(null);
  const [applicationData, setApplicationData] = useState({
    coverLetter: '',
    resumeId: '',
    phoneNumber: '',
    availability: 'IMMEDIATE',
    salary: '',
    additionalInfo: ''
  });
  const [profileCompleteness, setProfileCompleteness] = useState({
    percentage: 0,
    missingFields: []
  });

  // Fetch profile data whenever the modal is opened
  useEffect(() => {
    if (open) {
      fetchProfileData();
    }
  }, [open]);

  // Reset transient UI states whenever the modal is reopened
  useEffect(() => {
    if (open) {
      setError(null);
      setSuccess(false);
      setAlreadyApplied(false);
    }
  }, [open]);

  const fetchProfileData = async () => {
    setLoading(true);
    try {
      // Fetch jobseeker profile (uniform with CandidateDashboard)
      const response = await api.get('/api/jobseeker/profile');

      // Backend may return { success, data } wrapper or raw object â€“ normalise it
      const userData = response.data?.data || response.data || {};
      const candidateData = userData.candidateProfile || userData.candidate_profile || {};

      // Normalise resumes â€“ backend currently stores single resumeUrl/resumeFileName, but may use snake_case keys
      let resumes = [];
      if (Array.isArray(candidateData.resumes) && candidateData.resumes.length) {
        resumes = candidateData.resumes;
      } else {
        // support both camelCase and snake_case single-resume fields
        const singleResumeUrl = candidateData.resumeUrl || candidateData.resume_url;
        const singleResumeName = candidateData.resumeFileName || candidateData.resume_file_name;
        if (singleResumeUrl) {
          resumes = [{
            id: 'default',
            name: singleResumeName || 'Resume',
            uploadDate: candidateData.updatedAt || candidateData.updated_at || new Date().toISOString(),
            url: singleResumeUrl
          }];
        }
      }

      // Merge and normalise important fields for completeness check
      const mergedProfile = {
        ...candidateData,
        resumes,
        // Normalised phone number field
        phoneNumber: candidateData.phoneNumber || candidateData.phone_number || userData.phoneNumber || userData.phone_number || '',
        // Normalised experience array â€“ backend may use experience, workExperience, work_experience, experiences
        experience: 
          candidateData.experience || 
          candidateData.experiences || 
          candidateData.workExperience || 
          candidateData.work_experience || 
          userData.experience || 
          userData.workExperience || 
          userData.work_experience || []
      };

      setProfileData(mergedProfile);

      // Pre-fill application data from profile
      setApplicationData(prev => ({
        ...prev,
        phoneNumber: mergedProfile.phoneNumber,
        resumeId: resumes.length > 0 ? resumes[0].id : ''
      }));

      // Calculate profile completeness based on merged profile
      calculateProfileCompleteness(mergedProfile);

      // Log normalised profile data in development
      logDev('debug', 'Jobseeker profile fetched for quick apply', {
        resumeCount: resumes.length,
        hasPhone: !!(candidateData.phoneNumber || candidateData.phone_number || userData.phoneNumber || userData.phone_number),
        userId: currentUser?.id
      });
    } catch (error) {
      logError('Error fetching profile data for quick apply', error, {
        module: 'QuickApplyModal',
        function: 'fetchProfileData',
        userId: currentUser?.id,
        status: error.response?.status
      });
      setError('Failed to load your profile data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const calculateProfileCompleteness = (profile) => {
    const missingFields = [];
    let completedFields = 0;
    let totalFields = 0;

    // Check required fields â€“ support snake_case too
    const requiredFields = [
      { name: 'resume', value: profile.resumes && profile.resumes.length > 0 },
      { name: 'phoneNumber', value: profile.phoneNumber },
      { name: 'skills', value: profile.skills && profile.skills.length > 0 },
      { name: 'education', value: profile.education && profile.education.length > 0 },
      { 
        name: 'experience', 
        value: Array.isArray(profile.experience) 
          ? profile.experience.length > 0 
          : (typeof profile.experience === 'string' && profile.experience.trim().length > 0) 
      }
    ];

    requiredFields.forEach(field => {
      totalFields++;
      if (field.value) {
        completedFields++;
      } else {
        missingFields.push(field.name);
      }
    });

    const percentage = Math.round((completedFields / totalFields) * 100);
    setProfileCompleteness({
      percentage,
      missingFields
    });

    // Log profile completeness in development
    logDev('debug', 'Profile completeness calculated', {
      percentage,
      missingFields,
      userId: currentUser?.id
    });
  };

  const handleChange = (field) => (event) => {
    setApplicationData(prev => ({
      ...prev,
      [field]: event.target.value
    }));

    // Log field changes in development
    logDev('debug', 'Application form field changed', {
      field,
      fieldType: typeof event.target.value,
      valueLength: typeof event.target.value === 'string' ? event.target.value.length : 'N/A'
    });
  };

  const handleSubmit = async () => {
    setSubmitting(true);
    setError(null);

    // Log submission attempt in development
    logDev('debug', 'Validating application before submission', sanitizeForLogging({
      jobId: job?.id,
      resumeProvided: !!applicationData.resumeId,
      phoneProvided: !!applicationData.phoneNumber,
      coverLetterLength: applicationData.coverLetter?.length || 0,
      profileCompleteness: profileCompleteness.percentage
    }));

    try {
      // Create application data
      const payload = {
        jobId: job.id,
        coverLetter: applicationData.coverLetter,
        resumeId: applicationData.resumeId,
        phoneNumber: applicationData.phoneNumber,
        availability: applicationData.availability,
        salary: applicationData.salary,
        additionalInfo: applicationData.additionalInfo
      };

      // Log application payload in development (sanitized)
      logDev('debug', 'Submitting application', sanitizeForLogging(payload));

      // Submit application to backend
      const response = await api.post(`/api/jobs/${job.id}/apply`, payload);

      if (response.status === 201) {
        setSuccess(true);

        // Log successful submission
        logDev('info', 'Application submitted successfully', {
          jobId: job?.id,
          applicationId: response.data?.id,
          userId: currentUser?.id
        });

        // Call onSuccess after a delay to allow user to see success message
        setTimeout(() => {
          onSuccess(response.data);
        }, 2000);
      }
    } catch (err) {
      // Log submission error with context
      logError('Application submission failed', err, {
        module: 'QuickApplyModal',
        function: 'handleSubmit',
        jobId: job?.id,
        userId: currentUser?.id,
        status: err.response?.status,
        errorMessage: err.response?.data?.message
      });

      // Determine specific error type for better user feedback
      let errorMessage = 'Failed to submit application. Please try again.';
      if (err.response?.status === 400) {
        errorMessage = err.response.data?.message || 'Missing required information for application.';
      } else if (err.response?.status === 409) {
        // User has already applied â€“ show informational dialog instead of error
        setAlreadyApplied(true);
        return; // Skip setting generic error/UI flow
      } else if (err.response?.status === 403) {
        errorMessage = 'You do not have permission to apply for this job.';
      }

      setError(errorMessage);
    } finally {
      setSubmitting(false);
    }
  };

  const availabilityOptions = [
    { value: 'IMMEDIATE', label: 'Immediately' },
    { value: 'ONE_WEEK', label: 'One week notice' },
    { value: 'TWO_WEEKS', label: 'Two weeks notice' },
    { value: 'ONE_MONTH', label: 'One month notice' },
    { value: 'NEGOTIABLE', label: 'Negotiable' }
  ];

  const formatMissingFieldName = (fieldName) => {
    switch (fieldName) {
      case 'resume':
        return 'Resume';
      case 'phoneNumber':
        return 'Phone Number';
      case 'skills':
        return 'Skills';
      case 'education':
        return 'Education';
      case 'experience':
        return 'Work Experience';
      default:
        return fieldName;
    }
  };

  if (loading) {
    return (
      <Dialog open={open} onClose={onClose} maxWidth="md" fullWidth>
        <DialogContent>
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
            <CircularProgress sx={{ color: '#2C5530' }} />
          </Box>
        </DialogContent>
      </Dialog>
    );
  }

  if (success) {
    return (
      <Dialog 
        open={open} 
        onClose={() => {
          logDev('debug', 'Success dialog closed');
          onClose();
        }} 
        maxWidth="sm" 
        fullWidth
      >
        <DialogContent>
          <Box sx={{ py: 2, textAlign: 'center' }}>
            <CheckCircleIcon sx={{ fontSize: 60, color: '#4caf50', mb: 2 }} />
            <Typography variant="h5" gutterBottom>
              Thank you for applying!
            </Typography>
            <Typography variant="body1" sx={{ mb: 3 }}>
              Thank you for applying, your employer should be getting a notification.
            </Typography>
            <Button
              variant="contained"
              onClick={() => onClose()}
              sx={{
                background: 'linear-gradient(90deg, #2C5530, #FFD700)',
                color: '#000',
                '&:hover': {
                  background: 'linear-gradient(90deg, #FFD700, #2C5530)'
                }
              }}
            >
              Close
            </Button>
          </Box>
        </DialogContent>
      </Dialog>
    );
  }

  // Show dedicated message if the user has already applied for this job
  if (alreadyApplied) {
    return (
      <Dialog
        open={open}
        onClose={() => {
          logDev('debug', 'Already-applied dialog closed');
          onClose();
        }}
        maxWidth="sm"
        fullWidth
      >
        <DialogContent>
          <Box sx={{ py: 2, textAlign: 'center' }}>
            <InfoIcon sx={{ fontSize: 60, color: '#2196f3', mb: 2 }} />
            <Typography variant="h5" gutterBottom>
              You have already applied!
            </Typography>
            <Typography variant="body1" sx={{ mb: 3 }}>
              Our records show you already submitted an application for this position. The employer has been notified.
            </Typography>
            <Button
              variant="contained"
              onClick={() => onClose()}
              sx={{
                background: 'linear-gradient(90deg, #2C5530, #FFD700)',
                color: '#000',
                '&:hover': {
                  background: 'linear-gradient(90deg, #FFD700, #2C5530)'
                }
              }}
            >
              Close
            </Button>
          </Box>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog 
      open={open} 
      onClose={() => {
        logDev('debug', 'Application form closed without submission', {
          jobId: job?.id,
          formState: Object.keys(applicationData).length > 0 ? 'partially_filled' : 'empty' 
        });
        onClose();
      }} 
      maxWidth="md" 
      fullWidth
    >
      <DialogTitle sx={{ 
        borderBottom: '1px solid rgba(0, 0, 0, 0.12)',
        background: 'linear-gradient(90deg, rgba(44, 85, 48, 0.1), rgba(255, 215, 0, 0.1))'
      }}>
        <Typography variant="h6">Quick Apply: {job.title}</Typography>
        <Typography variant="subtitle2" color="text.secondary">
          {job.company?.name} â€¢ {job.location}
        </Typography>
      </DialogTitle>

      <DialogContent sx={{ pt: 3 }}>
        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        {profileCompleteness.percentage < 100 && (
          <Alert severity="warning" sx={{ mb: 3 }}>
            <Typography variant="subtitle2" sx={{ mb: 1 }}>
              Your profile is {profileCompleteness.percentage}% complete
            </Typography>
            <LinearProgress 
              variant="determinate" 
              value={profileCompleteness.percentage} 
              sx={{ 
                mb: 2, 
                height: 8, 
                borderRadius: 4,
                backgroundColor: 'rgba(255, 215, 0, 0.2)',
                '& .MuiLinearProgress-bar': {
                  backgroundColor: profileCompleteness.percentage < 50 ? '#f44336' : 
                                  profileCompleteness.percentage < 80 ? '#ff9800' : '#4caf50'
                }
              }} 
            />
            <Typography variant="body2">
              Complete these fields in your profile for better application success:
            </Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mt: 1 }}>
              {profileCompleteness.missingFields.map(field => (
                <Chip 
                  key={field}
                  label={formatMissingFieldName(field)}
                  size="small"
                  icon={<WarningIcon />}
                  sx={{ 
                    backgroundColor: 'rgba(255, 152, 0, 0.1)', 
                    borderColor: 'rgba(255, 152, 0, 0.5)',
                    color: '#ff9800'
                  }}
                />
              ))}
            </Box>
          </Alert>
        )}

        <Grid container spacing={3}>
          {/* Resume Selection */}
          <Grid item xs={12}>
            <FormControl fullWidth error={!applicationData.resumeId}>
              <InputLabel>Select Resume</InputLabel>
              <Select
                value={applicationData.resumeId}
              onChange={(e) => {
                handleChange('resumeId')(e);
                logDev('debug', 'Resume selected', { 
                  resumeId: e.target.value,
                  available: profileData?.resumes?.length || 0
                });
              }}
              label="Select Resume"
            >
                {profileData?.resumes?.length > 0 ? (
                  profileData.resumes.map(resume => (
                    <MenuItem key={resume.id} value={resume.id}>
                      {resume.name || 'Resume'} (Uploaded {new Date(resume.uploadDate).toLocaleDateString()})
                    </MenuItem>
                  ))
                ) : (
                  <MenuItem disabled value="">
                    No resumes uploaded
                  </MenuItem>
                )}
              </Select>
              {!applicationData.resumeId && (
                <Typography variant="caption" color="error">
                  A resume is required to apply
                </Typography>
              )}
            </FormControl>
          </Grid>

          {/* Cover Letter */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              multiline
              rows={4}
              label="Cover Letter (Optional)"
              value={applicationData.coverLetter}
              onChange={handleChange('coverLetter')}
              placeholder="Introduce yourself and explain why you're a good fit for this position..."
            />
          </Grid>

          {/* Phone Number */}
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Phone Number"
              value={applicationData.phoneNumber}
              onChange={handleChange('phoneNumber')}
              placeholder="+1 (876) 123-4567"
            />
          </Grid>

          {/* Availability */}
          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Availability</InputLabel>
              <Select
                value={applicationData.availability}
                onChange={handleChange('availability')}
                label="Availability"
              >
                {availabilityOptions.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          {/* Expected Salary */}
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Expected Salary (Optional)"
              value={applicationData.salary}
              onChange={handleChange('salary')}
              placeholder="e.g., $60,000 - $70,000"
            />
          </Grid>

          {/* Additional Info */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              multiline
              rows={2}
              label="Additional Information (Optional)"
              value={applicationData.additionalInfo}
              onChange={handleChange('additionalInfo')}
              placeholder="Any other information you'd like to share with the employer..."
            />
          </Grid>
        </Grid>
      </DialogContent>

      <DialogActions sx={{ px: 3, py: 2, borderTop: '1px solid rgba(0, 0, 0, 0.12)' }}>
        <Button 
          onClick={onClose}
          sx={{ color: '#2C5530' }}
        >
          Cancel
        </Button>
        <Button
          variant="contained"
          onClick={() => {
            logDev('debug', 'Submit button clicked', {
              profileCompleteness: profileCompleteness.percentage,
              hasResume: !!applicationData.resumeId,
              hasPhone: !!applicationData.phoneNumber
            });
            handleSubmit();
          }}
          disabled={submitting || !applicationData.resumeId || profileCompleteness.percentage < 50}
          sx={{
            background: 'linear-gradient(90deg, #2C5530, #FFD700)',
            color: '#000',
            '&:hover': {
              background: 'linear-gradient(90deg, #FFD700, #2C5530)'
            }
          }}
        >
          {submitting ? (
            <>
              <CircularProgress size={20} sx={{ mr: 1, color: '#000' }} />
              Submitting...
            </>
          ) : 'Submit Application'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default QuickApplyModal;

import React from 'react';
import {
  Box,
  Typography,
  Container,
  useTheme as useMuiTheme,
  useMediaQuery,
  Slide
} from '@mui/material';
import { useTheme } from '../../context/ThemeContext';
import { HeroLayout, PageLayout, SectionLayout, CardLayout } from './ResponsiveLayout';

const PageTemplate = ({
  title,
  subtitle,
  heroContent,
  children,
  showHero = true,
  heroHeight = 'auto',
  maxWidth = 'lg',
  fadeIn = true,
  slideDirection = 'up',
  ...props
}) => {
  const muiTheme = useMuiTheme();
  const { isDarkMode, jamaicanColors } = useTheme();
  const isMobile = useMediaQuery(muiTheme.breakpoints.down('md'));

  const heroSection = showHero && (
    <HeroLayout
      sx={{
        minHeight: heroHeight === 'auto' 
          ? { xs: '50vh', sm: '60vh', md: '70vh' }
          : heroHeight,
        display: 'flex',
