        )}

        {heroContent && (
          <Slide direction="up" in timeout={1200}>
            <Box>{heroContent}</Box>
          </Slide>
        )}
      </Box>
    </HeroLayout>
  );

  const mainContent = (
    <PageLayout maxWidth={maxWidth} {...props}>
      {children}
    </PageLayout>
  );

  if (fadeIn) {
    return (
      <Box>
        {heroSection}
        {mainContent}
      </Box>
    );
  }

  return (
    <Box>
      {heroSection}
      {mainContent}
    </Box>
  );
};

