} from 'recharts';
import { format, subDays, startOfDay, endOfDay } from 'date-fns';
import { ThemeContext } from '../../context/ThemeContext';
import { useAnalytics } from '../../hooks/usePayment';
import { formatCurrency } from '../../config/stripe.config';

const AdminAnalytics = () => {
  const { jamaicanColors } = useContext(ThemeContext);
  const [timeRange, setTimeRange] = useState('30d');
  const [refreshing, setRefreshing] = useState(false);

  const { 
    analytics, 
    loading, 
    error, 
    refreshAnalytics,
    exportAnalytics 
  } = useAnalytics(timeRange);

  const timeRangeOptions = [
    { value: '7d', label: 'Last 7 Days' },
    { value: '30d', label: 'Last 30 Days' },
    { value: '90d', label: 'Last 3 Months' },
    { value: '1y', label: 'Last Year' }
  ];

  const handleRefresh = async () => {
    setRefreshing(true);
    try {
      await refreshAnalytics();
    } finally {
      setRefreshing(false);
    }
  };

  const handleExport = async () => {
    try {
      await exportAnalytics(timeRange);
    } catch (err) {
      console.error('Export failed:', err);
    }
  };

  // KPI Card Component
  const KPICard = ({ title, value, change, icon, color, format = 'number' }) => {
    const isPositive = change >= 0;
    const formattedValue = format === 'currency' ? 
      formatCurrency(value * 100, 'USD') : 
      value.toLocaleString();

    return (
      <Card>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <Box>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                {title}
              </Typography>
              <Typography variant="h4" sx={{ color, fontWeight: 'bold' }}>
                {formattedValue}
              </Typography>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5, mt: 1 }}>
                {isPositive ? (
                  <TrendingUpIcon sx={{ color: 'success.main', fontSize: 16 }} />
                ) : (
                  <TrendingDownIcon sx={{ color: 'error.main', fontSize: 16 }} />
                )}
                <Typography 
                  variant="body2" 
                  sx={{ color: isPositive ? 'success.main' : 'error.main' }}
                >
                  {Math.abs(change).toFixed(1)}%
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  vs previous period
                </Typography>
              </Box>
            </Box>
            <Box sx={{ 
              bgcolor: `${color}20`, 
              p: 1.5, 
              borderRadius: 2,
              display: 'flex',
              alignItems: 'center'
            }}>
              {icon}
            </Box>
          </Box>
        </CardContent>
      </Card>
    );
  };

  if (loading && !analytics) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert severity="error" sx={{ m: 2 }}>
        Failed to load analytics: {error.message}
      </Alert>
    );
  }

  if (!analytics) {
    return (
      <Alert severity="info" sx={{ m: 2 }}>
        No analytics data available
      </Alert>
    );
  }

  const chartColors = [
    jamaicanColors.green,
    jamaicanColors.gold,
    jamaicanColors.red,
    '#2196F3',
    '#FF9800',
    '#9C27B0'
  ];

  return (
    <Box>
      {/* Header */}
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <AnalyticsIcon sx={{ color: jamaicanColors.green }} />
          <Typography variant="h5">Payment Analytics</Typography>
        </Box>

        <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
          <FormControl size="small" sx={{ minWidth: 150 }}>
            <InputLabel>Time Range</InputLabel>
            <Select
              value={timeRange}
              label="Time Range"
              onChange={(e) => setTimeRange(e.target.value)}
            >
              {timeRangeOptions.map((option) => (
                <MenuItem key={option.value} value={option.value}>
                  {option.label}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <Button
            variant="outlined"
            startIcon={<RefreshIcon />}
            onClick={handleRefresh}
            disabled={refreshing}
            size="small"
          >
            {refreshing ? 'Refreshing...' : 'Refresh'}
          </Button>

          <Button
            variant="outlined"
            startIcon={<DownloadIcon />}
            onClick={handleExport}
            size="small"
          >
            Export
          </Button>
        </Box>
      </Box>

      {/* KPI Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <KPICard
