      {/* User Authentication Check */}
      {!user && (
        <Alert severity="warning" sx={{ mb: 3 }}>
          Please log in to access your payment dashboard.
        </Alert>
      )}

      {user && !customerId && (
        <Alert severity="info" sx={{ mb: 3 }}>
