import React, { useState, useContext } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  CircularProgress,
  Chip,
  Divider
} from '@mui/material';
import {
  CreditCard as CreditCardIcon,
  Add as AddIcon,
  Delete as DeleteIcon,
  Edit as EditIcon,
  Star as StarIcon,
  StarBorder as StarBorderIcon,
  Security as SecurityIcon
} from '@mui/icons-material';
import { ThemeContext } from '../../context/ThemeContext';
import { usePaymentMethods } from '../../hooks/usePayment';
import { getPaymentMethodIcon } from '../../config/stripe.config';
import StripeProvider from './StripeProvider';
import PaymentMethodForm from './PaymentMethodForm';

const PaymentMethods = ({ customerId }) => {
  const { jamaicanColors } = useContext(ThemeContext);
  const { 
    paymentMethods, 
    loading, 
    error, 
    addPaymentMethod,
    updatePaymentMethod,
    deletePaymentMethod,
    setDefaultPaymentMethod
  } = usePaymentMethods(customerId);

  const [showAddDialog, setShowAddDialog] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const [selectedMethod, setSelectedMethod] = useState(null);
  const [actionLoading, setActionLoading] = useState(false);

  const handleAddPaymentMethod = async (paymentMethodData) => {
    setActionLoading(true);
    try {
      await addPaymentMethod(paymentMethodData);
      setShowAddDialog(false);
    } catch (err) {
      console.error('Failed to add payment method:', err);
    } finally {
      setActionLoading(false);
    }
  };

  const handleDeletePaymentMethod = async () => {
    if (!selectedMethod) return;

    setActionLoading(true);
    try {
      await deletePaymentMethod(selectedMethod.id);
      setShowDeleteDialog(false);
      setSelectedMethod(null);
    } catch (err) {
      console.error('Failed to delete payment method:', err);
    } finally {
      setActionLoading(false);
    }
  };

  const handleSetDefault = async (paymentMethodId) => {
    setActionLoading(true);
    try {
      await setDefaultPaymentMethod(paymentMethodId);
    } catch (err) {
      console.error('Failed to set default payment method:', err);
    } finally {
      setActionLoading(false);
    }
  };

  const formatCardNumber = (last4) => {
    return `â€¢â€¢â€¢â€¢ â€¢â€¢â€¢â€¢ â€¢â€¢â€¢â€¢ ${last4}`;
  };

  const getCardBrand = (brand) => {
    return brand.charAt(0).toUpperCase() + brand.slice(1);
  };

  if (loading && paymentMethods.length === 0) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box>
      <Card>
        <CardContent>
          {/* Header */}
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <CreditCardIcon sx={{ color: jamaicanColors.green }} />
              <Typography variant="h6">Payment Methods</Typography>
            </Box>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={() => setShowAddDialog(true)}
              sx={{ bgcolor: jamaicanColors.green }}
            >
              Add Payment Method
            </Button>
          </Box>

          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error.message}
            </Alert>
          )}

          {/* Payment Methods List */}
          {paymentMethods.length === 0 ? (
            <Box sx={{ textAlign: 'center', py: 4 }}>
              <CreditCardIcon sx={{ fontSize: 64, color: 'text.secondary', mb: 2 }} />
              <Typography variant="h6" color="text.secondary" gutterBottom>
                No Payment Methods
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                Add a payment method to make purchases and manage subscriptions
              </Typography>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={() => setShowAddDialog(true)}
                sx={{ bgcolor: jamaicanColors.green }}
              >
                Add Your First Payment Method
              </Button>
            </Box>
          ) : (
            <List>
              {paymentMethods.map((method, index) => (
                <React.Fragment key={method.id}>
                  <ListItem
                    sx={{
                      border: method.is_default ? `2px solid ${jamaicanColors.green}` : '1px solid #e0e0e0',
                      borderRadius: 2,
                      mb: 1,
                      bgcolor: method.is_default ? `${jamaicanColors.green}05` : 'transparent'
                    }}
                  >
                    <ListItemIcon>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <span style={{ fontSize: '24px' }}>
                          {getPaymentMethodIcon(method.type)}
                        </span>
                        {method.is_default && (
                          <StarIcon sx={{ color: jamaicanColors.gold, fontSize: 20 }} />
                        )}
                      </Box>
                    </ListItemIcon>

                    <ListItemText
                      primary={
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Typography variant="body1" sx={{ fontWeight: 500 }}>
                            {method.type === 'card' ? 
                              `${getCardBrand(method.card.brand)} ${formatCardNumber(method.card.last4)}` :
                              method.type.replace('_', ' ').toUpperCase()
                            }
                          </Typography>
                          {method.is_default && (
                            <Chip 
                              label="Default" 
                              size="small" 
                              sx={{ 
                                bgcolor: jamaicanColors.green, 
                                color: 'white',
                                height: 20,
                                fontSize: '0.75rem'
                              }} 
                            />
                          )}
                        </Box>
                      }
                      secondary={
                        <Box>
                          {method.type === 'card' && (
                            <Typography variant="body2" color="text.secondary">
                              Expires {method.card.exp_month.toString().padStart(2, '0')}/{method.card.exp_year}
                            </Typography>
                          )}
                          <Typography variant="caption" color="text.secondary">
                            Added {new Date(method.created * 1000).toLocaleDateString()}
                          </Typography>
                        </Box>
                      }
                    />

                    <ListItemSecondaryAction>
                      <Box sx={{ display: 'flex', gap: 0.5 }}>
                        {!method.is_default && (
                          <IconButton
                            size="small"
                            onClick={() => handleSetDefault(method.id)}
                            disabled={actionLoading}
                            title="Set as default"
                          >
                            <StarBorderIcon />
                          </IconButton>
                        )}
                        <IconButton
                          size="small"
                          onClick={() => {
                            setSelectedMethod(method);
                            setShowDeleteDialog(true);
                          }}
                          disabled={actionLoading}
                          color="error"
                          title="Delete payment method"
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Box>
                    </ListItemSecondaryAction>
                  </ListItem>
                  {index < paymentMethods.length - 1 && <Divider sx={{ my: 1 }} />}
                </React.Fragment>
              ))}
            </List>
          )}

          {/* Security Notice */}
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: 1, 
            mt: 3, 
            p: 2,
            bgcolor: '#f5f5f5',
            borderRadius: 1
          }}>
            <SecurityIcon sx={{ color: jamaicanColors.green }} />
            <Typography variant="body2" color="text.secondary">
              Your payment information is securely encrypted and stored by Stripe. 
              We never store your full card details on our servers.
            </Typography>
          </Box>
        </CardContent>
      </Card>

      {/* Add Payment Method Dialog */}
      <Dialog 
        open={showAddDialog} 
        onClose={() => setShowAddDialog(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <AddIcon sx={{ color: jamaicanColors.green }} />
            Add Payment Method
          </Box>
        </DialogTitle>
        <DialogContent>
          <StripeProvider>
            <PaymentMethodForm
              onSubmit={handleAddPaymentMethod}
              loading={actionLoading}
              onCancel={() => setShowAddDialog(false)}
            />
          </StripeProvider>
        </DialogContent>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={showDeleteDialog} onClose={() => setShowDeleteDialog(false)}>
        <DialogTitle>Delete Payment Method</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete this payment method? This action cannot be undone.
          </Typography>
          {selectedMethod?.is_default && (
            <Alert severity="warning" sx={{ mt: 2 }}>
              This is your default payment method. You'll need to set another payment method 
              as default before deleting this one.
            </Alert>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowDeleteDialog(false)}>
            Cancel
          </Button>
          <Button
            variant="contained"
            color="error"
            onClick={handleDeletePaymentMethod}
            disabled={actionLoading || selectedMethod?.is_default}
          >
            {actionLoading ? 'Deleting...' : 'Delete Payment Method'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default PaymentMethods;

import React, { useState, useContext, useEffect } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  Button,
  Chip,
  Grid,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  CircularProgress,
  Divider,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  LinearProgress
} from '@mui/material';
import {
  Subscriptions as SubscriptionsIcon,
  CheckCircle as CheckIcon,
  Cancel as CancelIcon,
  Upgrade as UpgradeIcon,
  Schedule as ScheduleIcon,
  Payment as PaymentIcon,
  Star as StarIcon,
  Verified as VerifiedIcon
} from '@mui/icons-material';
import { format, fromUnixTime, addMonths } from 'date-fns';
import { ThemeContext } from '../../context/ThemeContext';
import { useSubscription } from '../../hooks/usePayment';
import { formatCurrency, SUBSCRIPTION_PLANS, SUBSCRIPTION_STATUS } from '../../config/stripe.config';

const SubscriptionDashboard = ({ customerId }) => {
  const { jamaicanColors } = useContext(ThemeContext);
  const { 
    subscription, 
    updateSubscription, 
    cancelSubscription, 
    reactivateSubscription,
    loading, 
    error 
  } = useSubscription(customerId);

  const [showUpgradeDialog, setShowUpgradeDialog] = useState(false);
  const [showCancelDialog, setShowCancelDialog] = useState(false);
  const [selectedPlan, setSelectedPlan] = useState(null);
  const [actionLoading, setActionLoading] = useState(false);

  const getStatusColor = (status) => {
    switch (status) {
      case SUBSCRIPTION_STATUS.ACTIVE: return 'success';
      case SUBSCRIPTION_STATUS.TRIALING: return 'info';
      case SUBSCRIPTION_STATUS.PAST_DUE: return 'warning';
      case SUBSCRIPTION_STATUS.CANCELED: return 'error';
      default: return 'default';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case SUBSCRIPTION_STATUS.ACTIVE: return 'Active';
      case SUBSCRIPTION_STATUS.TRIALING: return 'Trial';
      case SUBSCRIPTION_STATUS.PAST_DUE: return 'Past Due';
      case SUBSCRIPTION_STATUS.CANCELED: return 'Canceled';
      case SUBSCRIPTION_STATUS.UNPAID: return 'Unpaid';
      default: return status;
    }
  };

  const handlePlanUpgrade = async (planId) => {
    setActionLoading(true);
    try {
      await updateSubscription(subscription.id, planId);
      setShowUpgradeDialog(false);
    } catch (err) {
      console.error('Upgrade failed:', err);
    } finally {
      setActionLoading(false);
    }
  };

  const handleCancelSubscription = async () => {
    setActionLoading(true);
    try {
      await cancelSubscription(subscription.id);
      setShowCancelDialog(false);
    } catch (err) {
      console.error('Cancellation failed:', err);
    } finally {
      setActionLoading(false);
    }
  };

  const handleReactivateSubscription = async () => {
    setActionLoading(true);
    try {
      await reactivateSubscription(subscription.id);
    } catch (err) {
      console.error('Reactivation failed:', err);
    } finally {
      setActionLoading(false);
    }
  };

  const getCurrentPlan = () => {
    if (!subscription?.plan) return null;
    return Object.values(SUBSCRIPTION_PLANS).find(plan => 
      plan.id === subscription.plan.id
    );
  };

  const getAvailableUpgrades = () => {
    const currentPlan = getCurrentPlan();
    if (!currentPlan) return [];

    return Object.values(SUBSCRIPTION_PLANS).filter(plan => 
      plan.prices.USD > currentPlan.prices.USD
    );
  };

  const calculateDaysRemaining = () => {
    if (!subscription?.current_period_end) return 0;
    const endDate = fromUnixTime(subscription.current_period_end);
    const now = new Date();
    const diffTime = endDate - now;
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!subscription) {
    return (
      <Card>
        <CardContent sx={{ textAlign: 'center', p: 4 }}>
          <SubscriptionsIcon sx={{ fontSize: 64, color: 'text.secondary', mb: 2 }} />
          <Typography variant="h6" gutterBottom>
            No Active Subscription
          </Typography>
          <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
            Subscribe to unlock premium features and HEART partnership benefits
          </Typography>
          <Button
            variant="contained"
            startIcon={<UpgradeIcon />}
            sx={{ bgcolor: jamaicanColors.green }}
            onClick={() => setShowUpgradeDialog(true)}
          >
            View Plans
          </Button>
        </CardContent>
      </Card>
    );
  }

  const currentPlan = getCurrentPlan();
  const daysRemaining = calculateDaysRemaining();
  const progressPercentage = subscription.current_period_start && subscription.current_period_end ?
    ((Date.now() / 1000 - subscription.current_period_start) / 
     (subscription.current_period_end - subscription.current_period_start)) * 100 : 0;

  return (
    <Box>
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error.message}
        </Alert>
      )}

      {/* Main Subscription Card */}
      <Card sx={{ mb: 3 }}>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <SubscriptionsIcon sx={{ color: jamaicanColors.green }} />
              <Typography variant="h6">
                {currentPlan?.name || 'Subscription'}
              </Typography>
              {currentPlan?.heartBenefits && (
                <VerifiedIcon sx={{ color: jamaicanColors.gold, ml: 1 }} />
              )}
            </Box>
            <Chip 
              label={getStatusText(subscription.status)}
              color={getStatusColor(subscription.status)}
              variant="outlined"
            />
          </Box>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                Current Plan
              </Typography>
              <Typography variant="h4" sx={{ color: jamaicanColors.green, fontWeight: 'bold' }}>
                {formatCurrency((currentPlan?.prices.USD || 0) * 100, 'USD')}
                <Typography component="span" variant="body1" color="text.secondary">
                  /month
                </Typography>
              </Typography>
            </Grid>

            <Grid item xs={12} md={6}>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                Next Billing Date
              </Typography>
              <Typography variant="h6">
                {subscription.current_period_end ? 
                  format(fromUnixTime(subscription.current_period_end), 'MMM dd, yyyy') :
                  'N/A'
                }
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {daysRemaining > 0 ? `${daysRemaining} days remaining` : 'Expired'}
              </Typography>
            </Grid>
          </Grid>

          {/* Billing Cycle Progress */}
          {subscription.status === SUBSCRIPTION_STATUS.ACTIVE && (
            <Box sx={{ mt: 3 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                <Typography variant="body2" color="text.secondary">
                  Billing Cycle Progress
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {Math.round(progressPercentage)}%
                </Typography>
              </Box>
              <LinearProgress 
                variant="determinate" 
                value={Math.min(progressPercentage, 100)}
                sx={{
                  height: 8,
                  borderRadius: 4,
                  bgcolor: '#f0f0f0',
                  '& .MuiLinearProgress-bar': {
                    bgcolor: jamaicanColors.green
                  }
                }}
              />
            </Box>
          )}

          {/* Plan Features */}
          {currentPlan && (
            <Box sx={{ mt: 3 }}>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                Plan Features
              </Typography>
              <List dense>
                {currentPlan.features.map((feature, index) => (
                  <ListItem key={index} sx={{ py: 0.5 }}>
                    <ListItemIcon sx={{ minWidth: 32 }}>
                      <CheckIcon sx={{ color: jamaicanColors.green, fontSize: 20 }} />
                    </ListItemIcon>
                    <ListItemText 
                      primary={feature}
                      primaryTypographyProps={{ variant: 'body2' }}
                    />
                  </ListItem>
                ))}
              </List>
            </Box>
          )}

          <Divider sx={{ my: 3 }} />

          {/* Action Buttons */}
          <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
            {subscription.status === SUBSCRIPTION_STATUS.ACTIVE && !subscription.cancel_at_period_end && (
              <>
                <Button
                  variant="contained"
                  startIcon={<UpgradeIcon />}
                  onClick={() => setShowUpgradeDialog(true)}
                  sx={{ bgcolor: jamaicanColors.green }}
                >
                  Upgrade Plan
                </Button>
                <Button
                  variant="outlined"
                  startIcon={<CancelIcon />}
                  onClick={() => setShowCancelDialog(true)}
                  color="error"
                >
                  Cancel Subscription
                </Button>
              </>
            )}

            {subscription.cancel_at_period_end && (
              <Button
                variant="contained"
                startIcon={<CheckIcon />}
                onClick={handleReactivateSubscription}
                disabled={actionLoading}
                sx={{ bgcolor: jamaicanColors.green }}
              >
                {actionLoading ? 'Reactivating...' : 'Reactivate Subscription'}
              </Button>
            )}

            {subscription.status === SUBSCRIPTION_STATUS.CANCELED && (
              <Button
                variant="contained"
                startIcon={<UpgradeIcon />}
                onClick={() => setShowUpgradeDialog(true)}
                sx={{ bgcolor: jamaicanColors.green }}
              >
                Subscribe Again
              </Button>
            )}
          </Box>

          {subscription.cancel_at_period_end && (
            <Alert severity="warning" sx={{ mt: 2 }}>
              Your subscription will be canceled at the end of the current billing period on{' '}
              {format(fromUnixTime(subscription.current_period_end), 'MMM dd, yyyy')}.
            </Alert>
          )}
        </CardContent>
      </Card>

      {/* Upgrade Dialog */}
      <Dialog 
        open={showUpgradeDialog} 
        onClose={() => setShowUpgradeDialog(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <UpgradeIcon sx={{ color: jamaicanColors.green }} />
            Choose Your Plan
          </Box>
        </DialogTitle>
        <DialogContent>
          <Grid container spacing={2}>
            {Object.values(SUBSCRIPTION_PLANS).map((plan) => (
              <Grid item xs={12} md={6} key={plan.id}>
                <Card 
                  variant="outlined"
                  sx={{ 
                    cursor: 'pointer',
                    border: selectedPlan?.id === plan.id ? 
                      `2px solid ${jamaicanColors.green}` : '1px solid #e0e0e0',
                    '&:hover': { boxShadow: 2 }
                  }}
                  onClick={() => setSelectedPlan(plan)}
                >
                  <CardContent>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                      {plan.popular && <StarIcon sx={{ color: jamaicanColors.gold }} />}
                      <Typography variant="h6">{plan.name}</Typography>
                      {plan.heartBenefits && (
                        <VerifiedIcon sx={{ color: jamaicanColors.green }} />
                      )}
                    </Box>

                    <Typography variant="h4" sx={{ color: jamaicanColors.green, mb: 1 }}>
                      {formatCurrency(plan.prices.USD * 100, 'USD')}
                      <Typography component="span" variant="body1" color="text.secondary">
                        /month
                      </Typography>
                    </Typography>

                    <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                      {plan.description}
                    </Typography>

                    <List dense>
                      {plan.features.slice(0, 4).map((feature, index) => (
                        <ListItem key={index} sx={{ py: 0.25 }}>
                          <ListItemIcon sx={{ minWidth: 24 }}>
                            <CheckIcon sx={{ color: jamaicanColors.green, fontSize: 16 }} />
                          </ListItemIcon>
                          <ListItemText 
                            primary={feature}
                            primaryTypographyProps={{ variant: 'body2' }}
                          />
                        </ListItem>
                      ))}
                    </List>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowUpgradeDialog(false)}>
            Cancel
          </Button>
          <Button
            variant="contained"
            onClick={() => selectedPlan && handlePlanUpgrade(selectedPlan.id)}
            disabled={!selectedPlan || actionLoading}
            sx={{ bgcolor: jamaicanColors.green }}
          >
            {actionLoading ? 'Updating...' : 'Update Plan'}
          </Button>
        </DialogActions>
      </Dialog>

      {/* Cancel Confirmation Dialog */}
      <Dialog open={showCancelDialog} onClose={() => setShowCancelDialog(false)}>
        <DialogTitle>Cancel Subscription</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to cancel your subscription? You'll continue to have access 
            until the end of your current billing period.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setShowCancelDialog(false)}>
            Keep Subscription
          </Button>
          <Button
            variant="contained"
            color="error"
            onClick={handleCancelSubscription}
            disabled={actionLoading}
          >
            {actionLoading ? 'Canceling...' : 'Cancel Subscription'}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default SubscriptionDashboard;

/**
 * Job Type Multi-Select Chips Component
 * Chip-based selection with visual feedback and "Clear all" functionality
 */

import React, { useCallback } from 'react';
import {
  Box,
  Typography,
  Chip,
  Stack,
  Button,
  Grid,
  Paper,
  Divider
} from '@mui/material';
import { 
  Work,
  Schedule,
  Assignment,
  Home,
  Business,
  AccessTime,
  Clear,
  CheckCircle
} from '@mui/icons-material';
import { useTheme } from '../../../context/ThemeContext';

const jobTypeOptions = [
  {
    value: 'full-time',
    label: 'Full-time',
    icon: Work,
    color: '#4CAF50',
    description: '40+ hours per week',
    popular: true
  },
  {
    value: 'part-time',
    label: 'Part-time',
    icon: Schedule,
    color: '#FF9800',
    description: 'Less than 40 hours',
    popular: true
  },
  {
    value: 'contract',
    label: 'Contract',
    icon: Assignment,
    color: '#2196F3',
    description: 'Fixed-term project',
    popular: true
  },
  {
    value: 'remote',
    label: 'Remote',
    icon: Home,
    color: '#9C27B0',
    description: 'Work from anywhere',
    popular: true
  },
  {
    value: 'hybrid',
    label: 'Hybrid',
    icon: Business,
    color: '#607D8B',
    description: 'Office + remote mix',
    popular: false
  },
  {
    value: 'temporary',
    label: 'Temporary',
    icon: AccessTime,
    color: '#795548',
    description: 'Short-term position',
    popular: false
  }
];

const JobTypeChips = ({ 
  value = [],
  onChange,
  disabled = false,
  maxSelections = 4,
  showDescriptions = true,
  compact = false 
}) => {
  const { jamaicanColors } = useTheme();

  /**
   * Handle job type selection/deselection
   */
  const handleToggle = useCallback((jobType) => {
    if (disabled) return;

    let newSelection;

    if (value.includes(jobType)) {
      // Remove from selection
      newSelection = value.filter(type => type !== jobType);
    } else {
      // Add to selection (check max limit)
      if (value.length >= maxSelections) {
        console.warn(`Maximum ${maxSelections} job types can be selected`);
        return;
      }
      newSelection = [...value, jobType];
    }

    onChange?.(newSelection);
    console.warn('ðŸ’¼ Job types selected:', newSelection);
  }, [value, onChange, disabled, maxSelections]);

  /**
   * Clear all selections
   */
  const handleClearAll = useCallback(() => {
    if (disabled) return;

    onChange?.([]);
    console.warn('ðŸ’¼ All job types cleared');
  }, [onChange, disabled]);

  /**
   * Select popular job types
   */
  const handleSelectPopular = useCallback(() => {
    if (disabled) return;

    const popularTypes = jobTypeOptions
      .filter(option => option.popular)
      .map(option => option.value)
      .slice(0, maxSelections);

    onChange?.(popularTypes);
    console.warn('ðŸ’¼ Popular job types selected:', popularTypes);
  }, [onChange, disabled, maxSelections]);

  if (compact) {
    // Compact layout for mobile
    return (
      <Box sx={{ width: '100%' }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Work sx={{ color: jamaicanColors.green, mr: 1 }} />
          <Typography variant="h6" sx={{ fontWeight: 600 }}>
            Job Type
          </Typography>
          {value.length > 0 && (
            <Chip
              size="small"
              label={`${value.length} selected`}
              sx={{ ml: 1, bgcolor: jamaicanColors.green, color: 'white' }}
            />
          )}
        </Box>

        <Stack direction="row" spacing={1} sx={{ flexWrap: 'wrap', gap: 1 }}>
          {jobTypeOptions.map((option) => {
            const IconComponent = option.icon;
            const isSelected = value.includes(option.value);

            return (
              <Chip
                key={option.value}
                icon={<IconComponent sx={{ fontSize: '1rem' }} />}
                label={option.label}
                onClick={() => handleToggle(option.value)}
                variant={isSelected ? 'filled' : 'outlined'}
                sx={{
                  minHeight: 44, // Touch-friendly
                  px: 2,
                  borderColor: option.color,
                  color: isSelected ? 'white' : option.color,
                  bgcolor: isSelected ? option.color : 'transparent',
                  '&:hover': {
                    bgcolor: `${option.color}20`
                  },
                  '& .MuiChip-icon': {
                    color: isSelected ? 'white' : option.color
                  }
                }}
                disabled={disabled}
              />
            );
          })}
        </Stack>

        {value.length > 0 && (
          <Button
            startIcon={<Clear />}
            onClick={handleClearAll}
            size="small"
            sx={{ 
              mt: 2,
              color: 'text.secondary',
              '&:hover': {
                bgcolor: 'action.hover'
              }
            }}
            disabled={disabled}
          >
            Clear All
          </Button>
        )}
      </Box>
    );
  }

  // Full layout with descriptions
  return (
    <Box sx={{ width: '100%' }}>
      {/* Header */}
      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Work sx={{ color: jamaicanColors.green, mr: 1 }} />
          <Typography variant="h6" sx={{ fontWeight: 600 }}>
            Job Type
          </Typography>
          {value.length > 0 && (
            <Chip
              size="small"
              label={`${value.length}/${maxSelections} selected`}
              sx={{ 
                ml: 2, 
                bgcolor: jamaicanColors.green, 
                color: 'white',
                fontSize: '0.75rem'
              }}
            />
          )}
        </Box>

        {/* Quick Actions */}
        <Stack direction="row" spacing={1}>
          {value.length === 0 && (
            <Button
              size="small"
              onClick={handleSelectPopular}
              sx={{ 
                color: jamaicanColors.green,
                '&:hover': {
                  bgcolor: `${jamaicanColors.green}10`
                }
              }}
              disabled={disabled}
            >
              Select Popular
            </Button>
          )}

          {value.length > 0 && (
            <Button
              startIcon={<Clear />}
              size="small"
              onClick={handleClearAll}
              sx={{ 
                color: 'text.secondary',
                '&:hover': {
                  bgcolor: 'action.hover'
                }
              }}
              disabled={disabled}
            >
              Clear All
            </Button>
          )}
        </Stack>
      </Box>

      {/* Job Type Options */}
      <Grid container spacing={2}>
        {jobTypeOptions.map((option) => {
          const IconComponent = option.icon;
          const isSelected = value.includes(option.value);
          const isDisabled = disabled || (!isSelected && value.length >= maxSelections);

          return (
            <Grid item xs={12} sm={6} md={4} key={option.value}>
              <Paper
                elevation={isSelected ? 3 : 1}
                sx={{
                  p: 2,
                  cursor: isDisabled ? 'not-allowed' : 'pointer',
                  border: `2px solid ${isSelected ? option.color : 'transparent'}`,
                  bgcolor: isSelected ? `${option.color}10` : 'background.paper',
                  opacity: isDisabled ? 0.6 : 1,
                  transition: 'all 0.2s ease-in-out',
                  '&:hover': isDisabled ? {} : {
                    elevation: 2,
                    border: `2px solid ${option.color}50`,
                    bgcolor: `${option.color}05`
                  },
                  minHeight: showDescriptions ? 100 : 80,
                  position: 'relative'
                }}
                onClick={isDisabled ? undefined : () => handleToggle(option.value)}
              >
                {/* Selection Indicator */}
                {isSelected && (
                  <CheckCircle
                    sx={{
                      position: 'absolute',
                      top: 8,
                      right: 8,
                      color: option.color,
                      fontSize: '1.2rem'
                    }}
                  />
                )}

                {/* Content */}
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                  <IconComponent 
                    sx={{ 
                      color: option.color, 
                      mr: 1,
                      fontSize: '1.5rem' 
                    }} 
                  />
                  <Typography variant="subtitle1" sx={{ fontWeight: 600 }}>
                    {option.label}
                  </Typography>
                  {option.popular && (
                    <Chip
                      label="Popular"
                      size="small"
                      sx={{
                        ml: 1,
                        height: 16,
                        fontSize: '0.6rem',
                        bgcolor: jamaicanColors.gold,
                        color: 'white'
                      }}
                    />
                  )}
                </Box>

                {showDescriptions && (
                  <Typography 
                    variant="body2" 
                    color="text.secondary"
                    sx={{ fontSize: '0.85rem' }}
                  >
                    {option.description}
                  </Typography>
                )}
              </Paper>
            </Grid>
          );
        })}
      </Grid>

      {/* Selection Limit Warning */}
      {value.length >= maxSelections && (
        <Box sx={{ 
          mt: 2, 
          p: 2, 
          bgcolor: 'warning.light', 
          borderRadius: 2,
          border: '1px solid',
          borderColor: 'warning.main'
        }}>
          <Typography variant="body2" sx={{ fontWeight: 600 }}>
            Maximum Selection Reached
          </Typography>
          <Typography variant="body2" color="text.secondary">
            You can select up to {maxSelections} job types. Remove some to add others.
          </Typography>
        </Box>
      )}

      {/* Helpful Info */}
      <Box sx={{ 
        mt: 3, 
        p: 2, 
        bgcolor: `${jamaicanColors.green}10`, 
        borderRadius: 2,
        border: `1px solid ${jamaicanColors.green}30`
      }}>
        <Typography variant="body2" sx={{ fontWeight: 600, mb: 1 }}>
          ðŸ’¡ Job Type Tips
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Select multiple types to see more opportunities. Many jobs offer flexible 
          arrangements that might fit your needs.
        </Typography>
      </Box>
    </Box>
  );
};

export default JobTypeChips;

/**
 * Smart Suggestions Component
 * Trending searches, quick actions, and personalized suggestions
 */

import React, { useState, useCallback } from 'react';
import {
  Box,
  Typography,
  Chip,
  Paper,
  Grid,
  Stack,
  Button,
  List,
  ListItemIcon,
  ListItemText,
  ListItemButton,
  Divider,
  Skeleton,
  Badge,
  Tooltip
} from '@mui/material';
import {
  TrendingUp,
  Search,
  History,
  Star,
  LocationOn,
  Work,
  Schedule,
  MonetizationOn,
  Lightbulb,
  Clear,
  Refresh
} from '@mui/icons-material';
import { useTheme } from '../../../context/ThemeContext';

const _SmartSuggestions = ({
  trendingSearches = [],
  recentSearches = [],
  quickActions = [],
  smartSuggestions = [],
  loading = false,
  onSearchSuggestion,
  onQuickAction,
  onClearHistory,
  onRefresh,
  compact = false,
  maxItems = 5
}) => {
  const { jamaicanColors } = useTheme();
  const [activeTab, setActiveTab] = useState('trending');

  /**
   * Handle suggestion click
   */
  const _handleSuggestionClick = useCallback((suggestion, _type) => {
    if (type === 'search') {
      onSearchSuggestion?.(suggestion);
      console.warn('ðŸ” Search suggestion clicked:', suggestion);
    } else if (type === 'action') {
      onQuickAction?.(suggestion);
      console.warn('âš¡ Quick action clicked:', suggestion);
    }
  }, [onSearchSuggestion, onQuickAction]);

  /**
   * Get icon for suggestion type
   */
  const _getSuggestionIcon = (type, _category) => {
    switch (type) {
      case 'location':
        return LocationOn;
      case 'industry':
        return Work;
      case 'jobType':
        return Schedule;
      case 'salary':
        return MonetizationOn;
      case 'search':
        return Search;
      default:
        return Lightbulb;
    }
  };

  /**
   * Render trending searches
   */
  const _renderTrendingSearches = () => {
    if (loading) {
      return (
        <Stack spacing={1}>
          {Array.from({ length: 5 }).map((_, index) => (
            <Skeleton key={index} variant="rectangular" height={40} sx={{ borderRadius: 1 }} />
          ))}
        </Stack>
      );
    }

    if (!trendingSearches.length) {
      return (
        <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center', py: 2 }}>
          No trending searches available
        </Typography>
      );
    }

    return (
      <Stack spacing={1}>
        {trendingSearches.slice(0, maxItems).map((search, _index) => {
          const _IconComponent = getSuggestionIcon(search.type);
          return (
            <Paper
              key={index}
              sx={{
                p: 2,
                cursor: 'pointer',
                transition: 'all 0.2s ease-in-out',
                border: '1px solid transparent',
                '&:hover': {
                  border: `1px solid ${jamaicanColors.green}50`,
                  bgcolor: `${jamaicanColors.green}05`,
                  transform: 'translateX(4px)'
                }
              }}
              onClick={() => handleSuggestionClick(search, 'search')}
            >
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <IconComponent sx={{ color: jamaicanColors.green, mr: 1.5, fontSize: '1.2rem' }} />
                  <Box>
                    <Typography variant="body2" sx={{ fontWeight: 600 }}>
                      {search.query}
                    </Typography>
                    {search.category && (
                      <Typography variant="caption" color="text.secondary">
                        {search.category}
                      </Typography>
                    )}
                  </Box>
                </Box>

                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  {search.count && (
                    <Chip
                      label={`${search.count} searches`}
                      size="small"
                      sx={{
                        bgcolor: `${jamaicanColors.green}20`,
                        color: jamaicanColors.green,
                        fontSize: '0.7rem',
                        mr: 1
                      }}
                    />
                  )}
                  <TrendingUp sx={{ color: jamaicanColors.green, fontSize: '1rem' }} />
                </Box>
              </Box>
            </Paper>
          );
        })}
      </Stack>
    );
  };

  /**
   * Render recent searches
   */
  const _renderRecentSearches = () => {
    if (loading) {
      return (
        <Stack spacing={1}>
          {Array.from({ length: 5 }).map((_, index) => (
            <Skeleton key={index} variant="rectangular" height={40} sx={{ borderRadius: 1 }} />
          ))}
        </Stack>
      );
    }

    if (!recentSearches.length) {
      return (
        <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center', py: 2 }}>
          No recent searches
        </Typography>
      );
    }

    return (
      <Box>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="subtitle2" sx={{ fontWeight: 600 }}>
            Recent Searches
          </Typography>
          <Button
            size="small"
            startIcon={<Clear />}
            onClick={onClearHistory}
            sx={{ color: 'text.secondary' }}
          >
            Clear
          </Button>
        </Box>

        <List dense>
          {recentSearches.slice(0, maxItems).map((search, _index) => {
            const _IconComponent = getSuggestionIcon(search.type);
            return (
              <ListItemButton
                key={index}
                onClick={() => handleSuggestionClick(search, 'search')}
                sx={{
                  borderRadius: 1,
                  mb: 0.5,
                  '&:hover': {
                    bgcolor: `${jamaicanColors.green}10`
                  }
                }}
              >
                <ListItemIcon sx={{ minWidth: 36 }}>
                  <IconComponent sx={{ fontSize: '1rem', color: 'text.secondary' }} />
                </ListItemIcon>
                <ListItemText
                  primary={search.query}
                  secondary={search.timestamp ? new Date(search.timestamp).toLocaleDateString() : null}
                  primaryTypographyProps={{ variant: 'body2' }}
                  secondaryTypographyProps={{ variant: 'caption' }}
                />
              </ListItemButton>
            );
          })}
        </List>
      </Box>
    );
  };

  /**
   * Render quick actions
   */
  const _renderQuickActions = () => {
    if (loading) {
      return (
        <Grid container spacing={1}>
          {Array.from({ length: 6 }).map((_, index) => (
            <Grid item xs={6} key={index}>
              <Skeleton variant="rectangular" height={60} sx={{ borderRadius: 2 }} />
            </Grid>
          ))}
        </Grid>
      );
    }

    if (!quickActions.length) {
      return (
        <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center', py: 2 }}>
          No quick actions available
        </Typography>
      );
    }

    return (
      <Grid container spacing={2}>
        {quickActions.slice(0, 6).map((action, _index) => {
          const _IconComponent = getSuggestionIcon(action.type);
          return (
            <Grid item xs={6} sm={4} key={index}>
              <Paper
                sx={{
                  p: 2,
                  textAlign: 'center',
                  cursor: 'pointer',
                  transition: 'all 0.2s ease-in-out',
                  border: '1px solid transparent',
                  '&:hover': {
                    border: `1px solid ${jamaicanColors.green}50`,
                    bgcolor: `${jamaicanColors.green}05`,
                    transform: 'translateY(-2px)'
                  }
                }}
                onClick={() => handleSuggestionClick(action, 'action')}
              >
                <Badge
                  badgeContent={action.count}
                  color="secondary"
                  sx={{
                    '& .MuiBadge-badge': {
                      bgcolor: jamaicanColors.green,
                      color: 'white',
                      fontSize: '0.7rem'
                    }
                  }}
                  invisible={!action.count}
                >
                  <IconComponent sx={{ color: jamaicanColors.green, fontSize: '1.5rem', mb: 1 }} />
                </Badge>
                <Typography variant="body2" sx={{ fontWeight: 600, fontSize: '0.8rem' }}>
                  {action.label}
                </Typography>
                {action.description && (
                  <Typography variant="caption" color="text.secondary">
                    {action.description}
                  </Typography>
                )}
              </Paper>
            </Grid>
          );
        })}
      </Grid>
    );
  };

  /**
   * Render smart suggestions
   */
  const _renderSmartSuggestions = () => {
    if (loading) {
      return (
        <Stack spacing={1}>
          {Array.from({ length: 3 }).map((_, index) => (
            <Skeleton key={index} variant="rectangular" height={60} sx={{ borderRadius: 2 }} />
          ))}
        </Stack>
      );
    }

    if (!smartSuggestions.length) {
      return (
        <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center', py: 2 }}>
          No smart suggestions available
        </Typography>
      );
    }

    return (
      <Stack spacing={2}>
        {smartSuggestions.slice(0, 3).map((suggestion, index) => (
          <Paper
            key={index}
            sx={{
              p: 2,
              cursor: 'pointer',
              transition: 'all 0.2s ease-in-out',
              border: `1px solid ${jamaicanColors.green}30`,
              bgcolor: `${jamaicanColors.green}05`,
              '&:hover': {
                bgcolor: `${jamaicanColors.green}10`,
                transform: 'translateX(4px)'
              }
            }}
            onClick={() => handleSuggestionClick(suggestion, 'search')}
          >
            <Box sx={{ display: 'flex', alignItems: 'flex-start' }}>
