  const muiTheme = useMuiTheme();

  return (
    <FormControl 
      fullWidth 
      error={!!error}
      sx={{
        mb: muiTheme.spacing(touchOptimized ? 3 : 2)
      }}
      {...props}
    >
      {label && (
        <FormLabel 
          required={required}
          sx={{
            mb: 1,
            fontWeight: 600,
            color: 'text.primary',
            '&.Mui-focused': {
              color: 'primary.main'
            }
          }}
        >
          {label}
        </FormLabel>
      )}
      {children}
      {(error || helperText) && (
        <FormHelperText sx={{ mt: 1 }}>
          {error || helperText}
        </FormHelperText>
      )}
    </FormControl>
  );
};

